mapper.select:
select /* COAAdmMapper.selectAdmList */ adm_seq , auth_cd , dept_cd , (select cd_nm from co_code_mst where cd_id='ADMIN_DEPT_CD' and cd=a.dept_cd) as dept_cd_nm , dept_nm , type_cd , (select cd_nm from co_code_mst where cd_id='ADMIN_AUTH_CD' and cd=a.auth_cd) as auth_cd_nm , id , name , use_yn , last_lgn_dtm from co_adm_mst a
order by reg_dtm desc
limit #{firstIndex}, #{recordCountPerPage}

