mapper.insert:
insert /* COBUserMenuMapper.insertMenu */ into co_menu_mst
(
	parnt_seq
, pstn
, lft_val
, rht_val
, dpth
, menu_type
, menu_nm
, sub_menu_nm
, adm_url
, user_url
, gnb_yn
, wnpp_yn
, seo_kwrd
, seo_cntn
, use_yn
, reg_id
, reg_ip
, reg_dtm
, mod_id
, mod_ip
, mod_dtm
)
values
	(
		#{parntSeq}
	, #{pstn}
	, IFNULL(#{rhtVal}, 1)
	, IFNULL(#{rhtVal} + 1, 2)
	, #{dpth}
	, #{menuType}
	, #{menuNm}
	, #{subMenuNm}
	, #{admUrl}
	, #{userUrl}
	, #{gnbYn}
	, #{wnppYn}
	, #{seoKwrd}
	, #{seoCntn}
	, #{useYn}
	, #{regId}
	, #{regIp}
	, now()
	, #{regId}
	, #{regIp}
	, now()
	)

mapper.update:
update /* COBUserMenuMapper.updateMenuNm */ co_menu_mst
set
	menu_nm = #{menuNm}
  , mod_id = #{regId}
  , mod_ip = #{regIp}
  , mod_dtm = NOW()
where
	menu_seq = #{menuSeq}

update /* COBUserMenuMapper.updateMenuInf */ co_menu_mst
set
	menu_type = #{menuType}
  , sub_menu_nm = #{subMenuNm}
  , adm_url = #{admUrl}
  , user_url = #{userUrl}
  , gnb_yn = #{gnbYn}
  , wnpp_yn = #{wnppYn}
  , seo_kwrd = #{seoKwrd}
  , seo_cntn = #{seoCntn}
  , use_yn = #{useYn}
  , mod_id = #{regId}
  , mod_ip = #{regIp}
  , mod_dtm = NOW()
where
	menu_seq = #{menuSeq}

update /* COBUserMenuMapper.setMenuMove1 */ co_menu_mst
set
	pstn = pstn - 1
where 1=1
  and parnt_seq = #{nodeParntSeq}
  and pstn  >  #{nodePstn}

update /* COBUserMenuMapper.setMenuMove2 */ co_menu_mst
set
	lft_val = lft_val - #{ndif}
where lft_val  >  #{nodeRhtVal}

update /* COBUserMenuMapper.setMenuMove3 */ co_menu_mst
set
rht_val = rht_val - #{ndif}
where rht_val  >  #{nodeLftVal}
and menu_seq not in

	#{nodeId}

update /* COBUserMenuMapper.setMenuMove4 */ co_menu_mst
set
pstn = pstn + 1
where parnt_seq = #{refSeq}
and pstn  >=  #{pstn}
and menu_seq not in

	#{nodeId}

update /* COBUserMenuMapper.setMenuMove5 */ co_menu_mst
set
	pstn = pstn + 1
where parnt_seq = #{refSeq}
  and pstn  >=  #{pstn}

update /* COBUserMenuMapper.setMenuMove6 */ co_menu_mst
set
lft_val = lft_val + #{ndif}
where 1=1
and lft_val  >=  #{refInd}
and menu_seq not in

	#{nodeId}

update /* COBUserMenuMapper.setMenuMove7 */ co_menu_mst
set
rht_val = rht_val + #{ndif}
where rht_val  >=  #{refInd}
and menu_seq not in

	#{nodeId}

update /* COBUserMenuMapper.setMenuMove8 */ co_menu_mst
set
	lft_val = lft_val + #{ndif}
where lft_val  >=  #{refInd}

update /* COBUserMenuMapper.setMenuMove9 */ co_menu_mst
set
	rht_val = rht_val + #{ndif}
where rht_val  >=  #{refInd}

update /* COBUserMenuMapper.setMenuMove10 */ co_menu_mst
set
	parnt_seq = #{refSeq}
  , pstn = #{pstn}
where menu_seq = #{menuSeq}

update /* COBUserMenuMapper.setMenuMove11 */ co_menu_mst
set
lft_val = lft_val - #{idif}
, rht_val = rht_val - #{idif}
, dpth = dpth - #{ldif}
where menu_seq in

	#{nodeId}

mapper.delete:
delete /* COBUserMenuMapper.deleteMenu */
from
	co_menu_mst
where   lft_val  >=  #{lftVal}
  and rht_val  <=  #{rhtVal}

mapper.update:
update /* COBUserMenuMapper.setDeleteUpdatePstn */ co_menu_mst
set
	pstn = pstn - 1
where  parnt_seq = #{parntSeq}
  and pstn  >  #{pstn}

mapper.select:
select /* COBUserMenuMapper.getChildData */
	menu_seq
	 , parnt_seq
	 , menu_nm
from (
		 select
			 fn_get_connect_by_parent(menu_seq) as id
			  , @level as level
		 from (
				  select
					  @start_with:=#{menuSeq}
					   , @id:=@start_with
					   , @level:=0
			  ) vars join co_menu_mst
		 where @id is not null
	 ) t1 join co_menu_mst t2 on t1.id = t2.menu_seq
where 1=1
  and menu_seq != #{menuSeq}
  and user_use_yn = 'Y'
  and level = 1
order by
	lft_val

mapper.delete:
delete /* COBUserMenuMapper.deleteAdmMenu */
from co_adm_menu_dtl
where menu_seq = #{parntSeq}

mapper.update:
update /* COBUserMenuMapper.updateMenuUrl */ co_menu_mst
set
	user_url = #{userUrl}
	, adm_url = #{admUrl}
where
	menu_seq = #{menuSeq}
