mapper.select:
select /* COEMenuRoleMapper.selectMenuRoleLogList */ mst.mod_seq , mst.adm_id , mst.adm_nm , mst.adm_dept as adm_dept_nm , mst.trgt_adm_id , mst.trgt_adm_nm , mst.trgt_adm_dept as trgt_adm_dept_nm , (select max(a.mod_seq) from co_adm_mod_mst a where a.mod_seq != mst.mod_seq and a.trgt_adm_id = mst.trgt_adm_id and a.mod_seq < mst.mod_seq) as bfre_mod_seq , mst.reg_dtm from co_adm_mod_mst mst
order by mst.mod_dtm desc
limit #{firstIndex}, #{recordCountPerPage}
