mapper.insert:
insert /* COFCodeMapper.insertcode */ into co_code_mst
	( parnt_seq
	, pstn
	, lft_val
	, rht_val
	, dpth
	, cd_id
	, cd
	, cd_nm
	, en_cd_nm
	, cn_cd_nm
	, use_yn
	, reg_id
	, reg_ip
	, reg_dtm
	, mod_id
	, mod_ip
	, mod_dtm
	)
values
	( #{parntSeq}
	, #{pstn}
	, IFNULL(#{rhtVal}, 1)
	, IFNULL(#{rhtVal} + 1, 2)
	, #{dpth}


		, #{cdId}


		, (select cd_id from co_code_mst a where cd_seq = #{parntSeq} limit 1)


	, #{cd}
	, #{cdNm}
    , #{enCdNm}
    , #{cnCdNm}
	, #{useYn}
	, #{regId}
	, #{regIp}
	, NOW()
	, #{regId}
	, #{regIp}
	, NOW()
	)

mapper.update:
update /* COFCodeMapper.updatecodenm */ co_code_mst
set
	  cd_nm = #{cdNm}
    , mod_id = #{regId}
	, mod_ip = #{regIp}
	, mod_dtm = NOW()
where
	cd_seq = #{cdSeq}


	select
		(select dpth from co_code_mst where cd_seq = #{cdSeq}) as dpth
		, lft_val AS lftVal
		, rht_val AS rhtVal
	FROM
		co_code_mst
	where  lft_vaL <= (select lft_val from co_code_mst where cd_seq = #{cdSeq})
		and rht_val >= (select rht_val from co_code_mst where cd_seq = #{cdSeq})
		and dpth = 1

update /* COFCodeMapper.updateCodeInf */ co_code_mst
set


		  cd_id = #{cdId}
		, use_yn = 'y'


		cd = #{cd}
		, use_yn = #{useYn}
		, en_cd_nm = #{enCdNm}
		, cn_cd_nm = #{cnCdNm}


    , mod_id = #{regId}
	, mod_ip = #{regIp}
	, mod_dtm = now()
WHERE 1=1
	and cd_seq = #{cdSeq}


		and (cd_id is null or cd_id = '')
		and not exists((select * from (select cd_seq from co_code_mst where cd_id = #{cdId}) a))


		and not exists((select * from (select cd_seq from co_code_mst where lft_val > #{lftVal} and rht_val < #{rhtVal} and cd = #{cd}) a))

	update /* COFCodeMapper.updateCdId */ co_code_mst
	set
		  cd_id = #{cdId}
	    , mod_id = #{regId}
		, mod_ip = #{regIp}
		, mod_dtm = now()
	where 1=1
		and lft_val > (select * from (select lft_val from co_code_mst where cd_seq = #{cdSeq}) a)
    	and rht_val < (select * from (select rht_val from co_code_mst where cd_seq = #{cdSeq}) a)

mapper.select:
select /* COFCodeMapper.getMoveExits */
	cd_seq
from
	co_code_mst
where 1=1
	and lft_val >= #{lftVal}
   	and rht_val <= #{rhtVal}
   	and cd_seq = #{refSeq}

mapper.update:
update /* COFCodeMapper.setCodeMove1 */ co_code_mst
set
	pstn = pstn - 1
where 1=1
	and parnt_seq = #{nodeParntSeq}
	and pstn > #{nodePstn}

update /* COFCodeMapper.setCodeMove2 */ co_code_mst
set
	lft_val = lft_val - #{ndif}
where 1=1
	and lft_val > #{nodeRhtVal}

		update /* COFCodeMapper.setCodeMove3 */ co_code_mst
		set
			rht_val = rht_val - #{ndif}
		where 1=1
			and rht_val > #{nodeLftVal}
			and cd_seq not in

            #{nodeId}

		update /* COFCodeMapper.setCodeMove4 */ co_code_mst
		set
			pstn = pstn + 1
		where 1=1
			and parnt_seq = #{refSeq}
			and pstn >= #{pstn}
			and cd_seq not in

            #{nodeId}

update /* COFCodeMapper.setCodeMove5 */ co_code_mst
set
	pstn = pstn + 1
where 1=1
	and parnt_seq = #{refSeq}
	and pstn >= #{pstn}

		update /* COFCodeMapper.setCodeMove6 */ co_code_mst
		set
			lft_val = lft_val + #{ndif}
		where 1=1
			and lft_val >= #{refInd}
			and cd_seq not in

            #{nodeId}

		update /* COFCodeMapper.setCodeMove7 */ co_code_mst
		set
			rht_val = rht_val + #{ndif}
		where 1=1
			and rht_val >= #{refInd}
			and cd_seq not in

            #{nodeId}

update /* COFCodeMapper.setCodeMove8 */ co_code_mst
set
	lft_val = lft_val + #{ndif}
where 1=1
	and lft_val >= #{refInd}

update /* COFCodeMapper.setCodeMove9 */ co_code_mst
set
	rht_val = rht_val + #{ndif}
where 1=1
	and rht_val >= #{refInd}

update /* COFCodeMapper.setCodeMove10 */ co_code_mst
set
	parnt_seq = #{refSeq}
	, pstn = #{pstn}
where 1=1
	and cd_seq = #{cdSeq}

		update /* COFCodeMapper.setCodeMove11 */ co_code_mst
		set
			lft_val = lft_val - #{idif}
			, rht_val = rht_val - #{idif}
			, dpth = dpth - #{ldif}
		where 1=1
			and cd_seq in

            #{nodeId}

mapper.delete:
delete /* COFCodeMapper.deleteCode */
from
	co_code_mst
where 1=1
	and lft_val >= #{lftVal}
   	and rht_val <= #{rhtVal}

mapper.update:
update /* COFCodeMapper.setDeleteUpdatePstn */ co_code_mst
set
	pstn = pstn -1
where 1=1
	and parnt_seq = #{parntSeq}
    and pstn > #{pstn}
