mapper.insert:
INSERT /* COGBoardMapper.insertBoard */ INTO CO_BOARD_MST
    (
    SEQ
    , LANG_CD
    , TITL
    , CNTS
    , TYPE_CD
    , YEAR
    , FILE_SEQ
    , PC_THUMB_FILE_SEQ
    , MO_THUMB_FILE_SEQ
    , READ_CNT
    , POST_STRT_DTM
    , POST_END_DTM
    , ODTM_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    , MAIN_YN
    , TOP_YN
    , FAQ_TYPE
    )
    VALUES
    (
    #{seq}
    , #{langCd}
    , #{titl}
    , #{cnts}
    , #{typeCd}
    , #{year}
    , #{fileSeq}
    , #{pcThumbFileSeq}
    , #{moThumbFileSeq}
    , 0
    , #{postStrtDtm}
    , #{postEndDtm}
    , #{odtmYn}
    , #{regId}
    , #{regIp}
    , NOW()
    , #{mainYn}
    , #{topYn}
    , #{faqType}
    )

mapper.update:
UPDATE  /*COGBoardDTO.updateBoard*/
    CO_BOARD_MST
SET
    TITL = #{titl}
    , CNTS = #{cnts}
    , FILE_SEQ = #{fileSeq}
    , PC_THUMB_FILE_SEQ = #{pcThumbFileSeq}
    , MO_THUMB_FILE_SEQ = #{moThumbFileSeq}
    , POST_STRT_DTM = #{postStrtDtm}
    , POST_END_DTM = #{postEndDtm}
    , ODTM_YN = #{odtmYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
    , MAIN_YN = #{mainYn}
    , TOP_YN = #{topYn}
    , FAQ_TYPE = #{faqType}
WHERE 1 = 1
    AND SEQ = #{detailsKey}
    AND TYPE_CD = #{typeCd}

mapper.delete:
DELETE /* COGBoardDTO.deleteBoard */
FROM
CO_BOARD_MST



            TYPE_CD = #{typeCd}
            AND SEQ IN

                #{delValueList}



            TYPE_CD = #{typeCd}
            AND	SEQ = #{detailsKey}
