mapper.insert:

	SELECT IFNULL(MAX(ver)+1,1) FROM CO_CNTS_MST WHERE MENU_SEQ = #{menuSeq}

INSERT /* COGCntsMapper.insertCnts */ INTO CO_CNTS_MST
	( SEQ
	, MENU_SEQ
	, VER
	, CNTS
	, JS_CNTS
	, PRCS_CD
	, REG_ID
	, REG_IP
	, REG_DTM
	)
	VALUES
	( #{seq}
	, #{menuSeq}
	, #{ver}
	, #{cnts}
	, #{jsCnts}
	, '30'
	, #{regId}
	, #{regIp}
	, NOW()
	)

mapper.select:
SELECT /* COGCntsMapper.selectCntsDtl */ SEQ , MENU_SEQ , VER , CNTS , JS_CNTS , PRCS_CD , REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_DTM , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_DTM FROM CO_CNTS_MST A WHERE MENU_SEQ = #{menuSeq}
AND PRCS_CD = 10
AND SEQ = #{detailsKey}

mapper.update:
UPDATE  /*COGCntsMapper.updateCnts*/
	CO_CNTS_MST
SET
   CNTS = #{cnts}
  , JS_CNTS = #{jsCnts}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE
    MENU_SEQ = #{menuSeq}
AND	SEQ = #{detailsKey}

mapper.delete:
DELETE /* COGCntsMapper.deleteCnts */
FROM
CO_CNTS_MST



			MENU_SEQ = #{menuSeq}
			AND SEQ IN

				#{delValueList}

			AND PRCS_CD != '10'


			MENU_SEQ = #{menuSeq}
			AND	SEQ = #{detailsKey}
			AND PRCS_CD != '10'

mapper.update:
UPDATE /* COGCntsMapper.updateCntsExpr */ CO_CNTS_MST
SET PRCS_CD = '20'
	, MOD_ID = #{modId}
	, MOD_IP = #{modIp}
	, MOD_DTM = NOW()
WHERE PRCS_CD = '10'
  AND MENU_SEQ = #{menuSeq}

UPDATE /* COGCntsMapper.updateCntsAprvl */ CO_CNTS_MST
SET PRCS_CD = '10'
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE MENU_SEQ = #{menuSeq}
  AND SEQ = #{detailsKey}

mapper.insert:

	SELECT IFNULL(MAX(VER)+1,1) FROM CO_CNTS_MST WHERE MENU_SEQ = #{menuSeq}

INSERT /* COGCntsMapper.insertCntsCopy */ INTO CO_CNTS_MST
( SEQ
, MENU_SEQ
, VER
, CNTS
, JS_CNTS
, PRCS_CD
, REG_ID
, REG_IP
, REG_DTM
)
VALUES
( #{seq}
, #{menuSeq}
, #{ver}
, #{cnts}
, #{jsCnts}
, '30'
, #{regId}
, #{regIp}
, NOW()
)
