mapper.update:
UPDATE  /*COGCntsMapper.updateUseCnts*/
	CO_CNTS_MST
SET PRCS_CD = '20'
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE PRCS_CD = '10'
  AND MENU_SEQ	= #{menuSeq}

mapper.insert:

	SELECT IFNULL(MAX(ver)+1,1) FROM CO_CNTS_MST WHERE MENU_SEQ = #{menuSeq}

INSERT /* COGCntsDAO.insertCnts */ INTO CO_CNTS_MST
	( SEQ
	, MENU_SEQ
	, VER
	, CNTS
	, JS_CNTS
	, PRCS_CD
	, REG_ID
	, REG_IP
	, REG_DTM
	, MOD_ID
	, MOD_IP
	, MOD_DTM
	)
	VALUES
	( #{detailsKey}
	, #{menuSeq}
	, #{ver}
	, #{cnts}
	, #{jsCnts}
	, '10'
	, #{regId}
	, #{regIp}
	, NOW()
	, ''
	, ''
	, null
	)

mapper.update:
UPDATE  /*COGCntsMapper.updateCnts*/
	CO_CNTS_MST
SET
   CNTS = #{cnts}
  , JS_CNTS = #{jsCnts}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE
    MENU_SEQ = #{menuSeq}
AND	SEQ = #{detailsKey}

mapper.delete:
DELETE /* COGCntsMapper.deleteCnts */
FROM
CO_CNTS_MST



			MENU_SEQ = #{menuSeq}
			AND SEQ IN

				#{delValueList}



			MENU_SEQ = #{menuSeq}
			AND	SEQ = #{detailsKey}
