mapper.select:
SELECT /* BDANoticeMapper.selectNoticeList */ NTFY_SEQ , TITL , READ_CNT , REG_DTM , CASE WHEN DATE_ADD(REG_DTM, INTERVAL 3 DAY) >= now() THEN 'Y' ELSE 'N' END AS NEW_POST_YN , NTFY_YN , EXPS_STRT_DTM , EXPS_END_DTM
, REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_IP , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_IP , MOD_DTM
, EXPS_YN FROM BD_NTFY_MST A WHERE 1=1
LIMIT #{firstIndex}, #{recordCountPerPage}
LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT /* BDANoticeMapper.selectNoticeDtl */ NTFY_SEQ , NTFY_YN , EXPS_STRT_DTM , EXPS_END_DTM , ODTM_YN , TITL , CNTN , FILE_SEQ , EXPS_YN
, #{srchVal} AS SRCH_VAL
, REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_IP , REG_DTM , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_IP , MOD_DTM FROM BD_NTFY_MST A WHERE NTFY_SEQ = #{detailsKey}

mapper.insert:
INSERT /* BDANoticeMapper.insertNotice */ INTO BD_NTFY_MST
    (
    NTFY_SEQ
    , NTFY_YN
    , EXPS_STRT_DTM
    , EXPS_END_DTM
    , ODTM_YN
    , TITL
    , CNTN
    , FILE_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{ntfySeq}
    , #{ntfyYn}
    , #{expsStrtDtm}
    , #{expsEndDtm}
    , #{odtmYn}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /*BDANoticeDTO.updateNotice*/
    BD_NTFY_MST
SET
    NTFY_YN = #{ntfyYn}
    , EXPS_STRT_DTM = #{expsStrtDtm}
    , EXPS_END_DTM = #{expsEndDtm}
    , ODTM_YN = #{odtmYn}
    , TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND NTFY_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDANoticeMapper.deleteNotice */
FROM
BD_NTFY_MST



            NTFY_SEQ IN

                #{delValueList}



            NTFY_SEQ = #{detailsKey}

mapper.update:
UPDATE /* BDANoticeMapper.updateNoticeReadCnt */  BD_NTFY_MST
SET READ_CNT = (SELECT A.READ_CNT
                FROM
                    (
                        SELECT READ_CNT + 1 AS READ_CNT
                        FROM BD_NTFY_MST
                        WHERE NTFY_SEQ = #{detailsKey}
                    ) AS A
                WHERE NTFY_SEQ = #{detailsKey})
WHERE NTFY_SEQ = #{detailsKey};

mapper.select:
SELECT /* BDANoticeMapper.selectNextAndPrevSeqVal */ ROW_NUM , NEXT_SEQ , (SELECT TITL FROM BD_NTFY_MST WHERE NTFY_SEQ = NEXT_SEQ) AS NEXT_TITL , PREV_SEQ , (SELECT TITL FROM BD_NTFY_MST WHERE NTFY_SEQ = PREV_SEQ) AS PREV_TITL FROM ( SELECT (SELECT COUNT(*) FROM BD_NTFY_MST B WHERE B.NTFY_SEQ <= A.NTFY_SEQ) AS ROW_NUM , (SELECT MIN(NTFY_SEQ) FROM BD_NTFY_MST C WHERE C.NTFY_SEQ > A.NTFY_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS NEXT_SEQ , (SELECT MAX(NTFY_SEQ) FROM BD_NTFY_MST D WHERE D.NTFY_SEQ < A.NTFY_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS PREV_SEQ FROM BD_NTFY_MST A WHERE NTFY_SEQ = #{detailsKey} ORDER BY NTFY_SEQ ) TB

SELECT /* BDANoticeMapper.selectNextRowNumInfo */ ROWNUM , NTFY_SEQ ,TITL ,REG_DTM ,MAIN_POST_YN FROM ( SELECT @rownum:=@rownum+1 AS ROWNUM , TB1.NTFY_SEQ , TB1.TITL , TB1.REG_DTM , TB1.MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'Y' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1 AND NTFY_YN = 'Y' AND ((EXPS_STRT_DTM <= SYSDATE() AND EXPS_END_DTM > DATE_ADD(now(), INTERVAL 1 minute)) OR ODTM_YN ='Y') AND EXPS_YN = 'Y'
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS A
UNION ALL
SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'N' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS B ) AS TB1 , (select @rownum:=0) temp ORDER BY MAIN_POST_YN DESC, REG_DTM DESC
) TTB WHERE rownum = #{rownum} - 1

SELECT /* BDANoticeMapper.selectPrevRowNumInfo */ ROWNUM , NTFY_SEQ ,TITL ,REG_DTM ,MAIN_POST_YN FROM ( SELECT @rownum:=@rownum+1 AS ROWNUM , TB1.NTFY_SEQ , TB1.TITL , TB1.REG_DTM , TB1.MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'Y' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1 AND NTFY_YN = 'Y' AND ((EXPS_STRT_DTM <= SYSDATE() AND EXPS_END_DTM > DATE_ADD(now(), INTERVAL 1 minute)) OR ODTM_YN ='Y') AND EXPS_YN = 'Y'
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS A
UNION ALL
SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'N' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS B ) AS TB1 , (select @rownum:=0) temp ORDER BY MAIN_POST_YN DESC, REG_DTM DESC
) TTB WHERE rownum = #{rownum} + 1

SELECT ROWNUM FROM ( SELECT @ROWNUM:=@ROWNUM+1 AS ROWNUM , TB1.NTFY_SEQ , TB1.TITL , TB1.REG_DTM , TB1.MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'Y' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1 AND NTFY_YN = 'Y' AND ((EXPS_STRT_DTM <= SYSDATE() AND EXPS_END_DTM > DATE_ADD(now(), INTERVAL 1 minute)) OR ODTM_YN ='Y') AND EXPS_YN = 'Y'
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS A
UNION ALL
SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , MAIN_POST_YN FROM ( SELECT NTFY_SEQ , TITL , READ_CNT , REG_DTM , NTFY_YN , ODTM_YN , EXPS_STRT_DTM , EXPS_END_DTM , 'N' AS MAIN_POST_YN FROM BD_NTFY_MST WHERE 1=1
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS B ) AS TB1 , (select @ROWNUM:=0) TEMP ORDER BY MAIN_POST_YN DESC, REG_DTM DESC ) TB
WHERE NTFY_SEQ = #{detailsKey} AND MAIN_POST_YN = #{mainPostYn}
