mapper.select:
SELECT /* BDBCompanyNewsMapper.selectCompanyNewsDtl */ FNDN_NEWS_SEQ , TITL , CNTN , FILE_SEQ , PC_THNL_SEQ , MBL_THNL_SEQ , EXPS_YN
, #{srchVal} AS SRCH_VAL
, REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_IP , REG_DTM , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_IP , MOD_DTM FROM BD_FNDN_NEWS_MST A WHERE FNDN_NEWS_SEQ = #{detailsKey}

mapper.insert:
INSERT /* BDBCompanyNewsMapper.insertCompanyNews */ INTO BD_FNDN_NEWS_MST
    (
    FNDN_NEWS_SEQ
    , TITL
    , CNTN
    , FILE_SEQ
    , PC_THNL_SEQ
    , MBL_THNL_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{fndnNewsSeq}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , #{pcThnlSeq}
    , #{mblThnlSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /* BDBCompanyNewsMapper.updateCompanyNews */
    BD_FNDN_NEWS_MST
SET
    TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , PC_THNL_SEQ = #{pcThnlSeq}
    , MBL_THNL_SEQ = #{mblThnlSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND FNDN_NEWS_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDBCompanyNewsMapper.deleteCompanyNews */
FROM
BD_FNDN_NEWS_MST



            FNDN_NEWS_SEQ IN

                #{delValueList}



            FNDN_NEWS_SEQ = #{detailsKey}

mapper.update:
UPDATE /* BDBCompanyNewsMapper.updateCompanyNewsReadCnt */  BD_FNDN_NEWS_MST
SET READ_CNT = (SELECT A.READ_CNT
                FROM
                    (
                        SELECT READ_CNT + 1 AS READ_CNT
                        FROM BD_FNDN_NEWS_MST
                        WHERE FNDN_NEWS_SEQ = #{detailsKey}
                    ) AS A
                WHERE FNDN_NEWS_SEQ = #{detailsKey})
WHERE FNDN_NEWS_SEQ = #{detailsKey};

mapper.select:
SELECT /* BDBCompanyNewsMapper.selectNextAndPrevSeqVal */ ROW_NUM , NEXT_SEQ , (SELECT TITL FROM BD_FNDN_NEWS_MST WHERE FNDN_NEWS_SEQ = NEXT_SEQ) AS NEXT_TITL , PREV_SEQ , (SELECT TITL FROM BD_FNDN_NEWS_MST WHERE FNDN_NEWS_SEQ = PREV_SEQ) AS PREV_TITL FROM ( SELECT (SELECT COUNT(*) FROM BD_FNDN_NEWS_MST B WHERE B.FNDN_NEWS_SEQ <= A.FNDN_NEWS_SEQ) AS ROW_NUM , (SELECT MIN(FNDN_NEWS_SEQ) FROM BD_FNDN_NEWS_MST C WHERE C.FNDN_NEWS_SEQ > A.FNDN_NEWS_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS NEXT_SEQ , (SELECT MAX(FNDN_NEWS_SEQ) FROM BD_FNDN_NEWS_MST D WHERE D.FNDN_NEWS_SEQ < A.FNDN_NEWS_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS PREV_SEQ FROM BD_FNDN_NEWS_MST A WHERE FNDN_NEWS_SEQ = #{detailsKey} ORDER BY FNDN_NEWS_SEQ ) TB
