mapper.insert:
INSERT /* BDBCompanyNewsMapper.insertCompanyNews */ INTO BD_FNDN_NEWS_MST
    (
    FNDN_NEWS_SEQ
    , TITL
    , CNTN
    , FILE_SEQ
    , PC_THNL_SEQ
    , MBL_THNL_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{fndnNewsSeq}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , #{pcThnlSeq}
    , #{mblThnlSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /* BDBCompanyNewsMapper.updateCompanyNews */
    BD_FNDN_NEWS_MST
SET
    TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , PC_THNL_SEQ = #{pcThnlSeq}
    , MBL_THNL_SEQ = #{mblThnlSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND FNDN_NEWS_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDBCompanyNewsMapper.deleteCompanyNews */
FROM
BD_FNDN_NEWS_MST



            FNDN_NEWS_SEQ IN

                #{delValueList}



            FNDN_NEWS_SEQ = #{detailsKey}
