mapper.insert:
INSERT /* BDCFaqMapper.insertFaq */ INTO BD_FAQ_MST
    (
    FAQ_SEQ
    , CTGRY_CD
    , TITL
    , CNTN
    , FILE_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{faqSeq}
    , #{ctgryCd}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /*BDCFaqMapper.updateFaq*/
    BD_FAQ_MST
SET
    CTGRY_CD = #{ctgryCd}
    , TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND FAQ_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDCFaqMapper.deleteFaq */
FROM
BD_FAQ_MST



            FAQ_SEQ IN

                #{delValueList}



            AND	FAQ_SEQ = #{detailsKey}
