mapper.select:
SELECT /* BDDNewsletterMapper.selectNewsletterDtl */ NWSLT_SEQ , TITL , CNTN , FILE_SEQ , PC_THNL_SEQ , MBL_THNL_SEQ , EXPS_YN
, #{srchVal} AS SRCH_VAL
, REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_IP , REG_DTM , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_IP , MOD_DTM FROM BD_NWSLT_MST A WHERE NWSLT_SEQ = #{detailsKey}

mapper.insert:
INSERT /* BDDNewsletterMapper.insertNewsletter */ INTO BD_NWSLT_MST
    (
    NWSLT_SEQ
    , TITL
    , CNTN
    , FILE_SEQ
    , PC_THNL_SEQ
    , MBL_THNL_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{nwsltSeq}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , #{pcThnlSeq}
    , #{mblThnlSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /* BDDNewsletterMapper.updateNewsletter */
    BD_NWSLT_MST
SET
    TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , PC_THNL_SEQ = #{pcThnlSeq}
    , MBL_THNL_SEQ = #{mblThnlSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND NWSLT_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDDNewsletterMapper.deleteNewsletter */
FROM
BD_NWSLT_MST



            NWSLT_SEQ IN

                #{delValueList}



            NWSLT_SEQ = #{detailsKey}

mapper.update:
UPDATE /* BDDNewsletterMapper.updateNewsletterReadCnt */  BD_NWSLT_MST
SET READ_CNT = (SELECT A.READ_CNT
                FROM
                (
                    SELECT READ_CNT + 1 AS READ_CNT
                    FROM BD_NWSLT_MST
                    WHERE NWSLT_SEQ = #{detailsKey}
                ) AS A
                WHERE NWSLT_SEQ = #{detailsKey})
WHERE NWSLT_SEQ = #{detailsKey};

mapper.select:
SELECT /* BDDNewsletterMapper.selectNextAndPrevSeqVal */ ROW_NUM , NEXT_SEQ , (SELECT TITL FROM BD_NWSLT_MST WHERE NWSLT_SEQ = NEXT_SEQ) AS NEXT_TITL , PREV_SEQ , (SELECT TITL FROM BD_NWSLT_MST WHERE NWSLT_SEQ = PREV_SEQ) AS PREV_TITL FROM ( SELECT (SELECT COUNT(*) FROM BD_NWSLT_MST B WHERE B.NWSLT_SEQ <= A.NWSLT_SEQ) AS ROW_NUM , (SELECT MIN(NWSLT_SEQ) FROM BD_NWSLT_MST C WHERE C.NWSLT_SEQ > A.NWSLT_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS NEXT_SEQ , (SELECT MAX(NWSLT_SEQ) FROM BD_NWSLT_MST D WHERE D.NWSLT_SEQ < A.NWSLT_SEQ
AND (TITL LIKE CONCAT('%', #{srchVal}, '%') OR CNTN LIKE CONCAT('%', #{srchVal}, '%'))
) AS PREV_SEQ FROM BD_NWSLT_MST A WHERE NWSLT_SEQ = #{detailsKey} ORDER BY NWSLT_SEQ ) TB
