mapper.insert:
INSERT /* BDEDisclosureMapper.insertDisclosure */ INTO BD_MNGDSCLSR_MST
    (
    DSCLSR_SEQ
    , TITL
    , CNTN
    , FILE_SEQ
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{dsclsrSeq}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /*BDEDisclosureDTO.updateDisclosure*/
    BD_MNGDSCLSR_MST
SET
    TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND DSCLSR_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDEDisclosureMapper.deleteDisclosure */
FROM
BD_MNGDSCLSR_MST



            DSCLSR_SEQ IN

                #{delValueList}



            DSCLSR_SEQ = #{detailsKey}
