mapper.insert:
INSERT /* BDFOnlineMapper.insertOnline */ INTO BD_MNL_MST
    (
    MNL_SEQ
    , CTGRY_CD
    , TITL
    , CNTN
    , FILE_SEQ
    , READ_CNT
    , EXPS_YN
    , REG_ID
    , REG_IP
    , REG_DTM
    )
    VALUES
    (
    #{mnlSeq}
    , #{ctgryCd}
    , #{titl}
    , #{cntn}
    , #{fileSeq}
    , 0
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE  /*BDFOnlineMapper.updateOnline*/
    BD_MNL_MST
SET
    CTGRY_CD = #{ctgryCd}
    , TITL = #{titl}
    , CNTN = #{cntn}
    , FILE_SEQ = #{fileSeq}
    , EXPS_YN = #{expsYn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1 = 1
    AND MNL_SEQ = #{detailsKey}

mapper.delete:
DELETE /* BDFOnlineMapper.deleteOnline */
FROM
BD_MNL_MST



            MNL_SEQ IN

                #{delValueList}



            MNL_SEQ = #{detailsKey}
