mapper.select:
SELECT /* EBACouseMapper.selectCouseList */ edctn_seq , nm , parnt_seq , (select cd_nm from co_code_mst WHERE cd_seq = aa.parnt_seq) AS prnt_cd_nm , ctgry_cd , ctgry_cd_nm , stduy_mthd_cd , stduy_mthd_cd_nm , stduy_dd_cd , stduy_dd_cd_nm , stduy_time_cd , stduy_time_cd_nm , reg_id , reg_name , reg_dtm , mod_id , exps_yn FROM (select edctn_seq , nm , a.ctgry_cd , (select cd_nm from co_code_mst WHERE cd = a.ctgry_cd) as ctgry_cd_nm , (select parnt_seq from co_code_mst WHERE cd = a.ctgry_cd) as parnt_seq , stduy_mthd_cd , (select cd_nm from co_code_mst where cd=a.stduy_mthd_cd) as stduy_mthd_cd_nm , stduy_dd_cd , (select cd_nm from co_code_mst where cd=a.stduy_dd_cd) as stduy_dd_cd_nm , stduy_time_cd , (select cd_nm from co_code_mst where cd=a.stduy_time_cd) as stduy_time_cd_nm , reg_id , (select name from co_adm_mst where id = a.reg_id) as reg_name , reg_dtm , mod_id , exps_yn from ed_edctn_prcs_mst a) aa
order by reg_dtm desc
limit #{firstIndex}, #{recordCountPerPage}

mapper.insert:
