mapper.select:
SELECT edctn_prcs_mst.nm , appctn_mst.exam_appctn_seq , edctn_prcs_mst.stduy_mthd_cd , (select cd_nm from co_code_mst where cd = edctn_prcs_mst.stduy_mthd_cd and cd_id = 'STDUY_MTHD') stduy_mthd_cd_nm , edctn_prcs_mst.ctgry_cd , (SELECT GROUP_CONCAT(cd_nm SEPARATOR ' > ') FROM co_code_mst WHERE lft_val <= (SELECT lft_val FROM co_code_mst WHERE cd = 'CLASS01002') AND rht_val >= (SELECT rht_val FROM co_code_mst WHERE cd = 'CLASS01002') AND dpth < 4 AND dpth > 1) ctgry_cd_nm , edctn_prcs_mst.stduy_time_cd , (select cd_nm from co_code_mst where cd = edctn_prcs_mst.stduy_time_cd and cd_id = 'STDUY_TIME') stduy_time_cd_nm , edctn_prcs_mst.stduy_dd_cd , (select cd_nm from co_code_mst where cd = edctn_prcs_mst.stduy_dd_cd and cd_id = 'STDUY_DD') stduy_dd_cd_nm , edctn_episd_mst.edctn_seq , edctn_episd_mst.episd_year , edctn_episd_mst.episd_ord , edctn_episd_mst.cbsn_cd , (select code_mst.cd_nm from co_code_mst code_mst where code_mst.cd = edctn_episd_mst.cbsn_cd and code_mst.cd_id = 'CBSN_CD') cbsn_cd_nm , edctn_episd_mst.accs_strt_dtm , edctn_episd_mst.accs_end_dtm , edctn_episd_mst.edctn_strt_dtm , edctn_episd_mst.edctn_end_dtm , (SELECT GROUP_CONCAT(NAME SEPARATOR ', ') FROM cp_isttr_mst WHERE exists( SELECT isttr_seq FROM ed_edctn_isttr_rel WHERE edctn_seq=edctn_episd_mst.edctn_seq AND episd_year=edctn_episd_mst.episd_year AND episd_ord=edctn_episd_mst.episd_ord)) isttr_nm , edctn_episd_mst.fxnum_cnt , edctn_episd_mst.rcrmt_mthd_cd , (select cd_nm from co_code_mst where cd = edctn_episd_mst.rcrmt_mthd_cd and cd_id = 'RCRMT_MTHD') rcrmt_mthd_cd_nm , edctn_episd_mst.pic_nm , edctn_episd_mst.pic_email , edctn_episd_mst.pic_tel_no , edctn_episd_mst.place_seq , (select nm from ed_edctn_place_mst where place_seq = edctn_episd_mst.place_seq) place_nm , ptcpt_mst.gpc_id , appctn_mst.exam_cd , appctn_mst.issue_cd , appctn_mst.rtrn_rsn , appctn_mst.reg_id , appctn_mst.reg_dtm , appctn_mst.mod_id , appctn_mst.mod_dtm , appctn_mst.mem_seq , ptcpt_mst.cmptn_yn , ptcpt_mst.cmptn_dtm , appctn_mst.jdgmt_no
, appctn_mst.idntfn_photo_file_seq , appctn_mst.acqsn_dtm , appctn_mst.valid_strt_dt , appctn_mst.valid_end_dt FROM sq_exam_appctn_mst appctn_mst JOIN sq_exam_appctn_ptcpt_mst appctn_ptcpt_mst ON appctn_ptcpt_mst.exam_appctn_seq = appctn_mst.exam_appctn_seq join ep_edctn_ptcpt_mst ptcpt_mst on ptcpt_mst.ptcpt_seq = appctn_ptcpt_mst.ptcpt_seq and exists(select 1 from sq_exam_appctn_ptcpt_mst sq_ptcpt_mst where sq_ptcpt_mst.ptcpt_seq = ptcpt_mst.ptcpt_seq and exists( select edctn_seq from ed_edctn_prcs_mst where ctgry_cd = 'CLASS01002' and edctn_seq = ptcpt_mst.edctn_seq ) ) JOIN ed_edctn_episd_mst edctn_episd_mst ON edctn_episd_mst.edctn_seq = ptcpt_mst.edctn_seq AND edctn_episd_mst.episd_year = ptcpt_mst.episd_year AND edctn_episd_mst.episd_ord = ptcpt_mst.episd_ord JOIN ed_edctn_prcs_mst edctn_prcs_mst ON edctn_prcs_mst.edctn_seq = edctn_episd_mst.edctn_seq
WHERE appctn_ptcpt_mst.exam_appctn_seq = #{detailsKey};

select /*EBDSqCertiReqMapper.selectExamAppctnMst*/ appctn_mst.exam_appctn_seq , appctn_mst.mem_seq , appctn_mst.exam_cd , (select cd_nm from co_code_mst where cd = appctn_mst.exam_cd and cd_id = 'EBD_SQ_TP') exam_cd_nm , appctn_mst.issue_cd , appctn_mst.rtrn_rsn , appctn_mst.acqsn_dtm , appctn_mst.rfrsh_dtm , appctn_mst.valid_strt_dt , appctn_mst.valid_end_dt , appctn_mst.idntfn_photo_file_seq , appctn_mst.jdgmt_no , appctn_mst.bsnm_no , appctn_mst.use_yn , appctn_mst.reg_id , appctn_mst.reg_dtm , appctn_mst.mod_id , appctn_mst.mod_dtm , appctn_mst.mem_seq , cmpn_mst.cmpn_nm , mem_mst.name , mem_mst.birth , (select web_path from co_file_dtl where file_seq = appctn_mst.idntfn_photo_file_seq and use_yn = 'Y' order by reg_dtm desc limit 1) as idntfnPhotoFileSeqUrl FROM sq_exam_appctn_mst appctn_mst join co_mem_mst mem_mst on mem_mst.mem_seq = appctn_mst.mem_seq left join cp_cmpn_mst cmpn_mst on cmpn_mst.bsnm_no = appctn_mst.bsnm_no WHERE
appctn_mst.exam_appctn_seq = #{detailsKey} and appctn_mst.issue_cd = 'EBD_SQ_I'
appctn_mst.mem_seq = #{memSeq}

mapper.update:
update /* EBDSqCertiReqMapper.updateIssue */ sq_exam_appctn_mst
set
	exam_cd = #{examCd}
  , issue_cd = #{issueCd}

  , rtrn_rsn = #{rtrnRsn}


  , acqsn_dtm = now()
  , rfrsh_dtm = now()
  , valid_strt_dt = now()
  , valid_end_dt =  date_add(now(), INTERVAL 3 YEAR)
  , jdgmt_no = #{jdgmtNo}

  , mod_id = #{modId}
  , mod_ip = #{modIp}
  , mod_dtm = now()
WHERE exam_appctn_seq = #{detailsKey}

update /* EBDSqCertiReqMapper.updateConfirmInfo */ sq_exam_appctn_mst
set
  exam_cd = #{examCd}
, idntfn_photo_file_seq = #{idntfnPhotoFileSeq}
, bsnm_no = #{bsnmNo}
, use_yn = #{useYn}
, mod_id = #{modId}
, mod_ip = #{modIp}
, mod_dtm = now()
WHERE exam_appctn_seq = #{examAppctnSeq}

mapper.insert:
insert into /*EBBEpisdMapper.insertEpisd*/ sq_exam_appctn_mst (
	  exam_appctn_seq
	, mem_seq
	, exam_cd
	, issue_cd
	, idntfn_photo_file_seq
	, bsnm_no
	, use_yn
	, reg_id
	, reg_ip
	, reg_dtm
	, mod_id
	, mod_ip
	, mod_dtm
)
values
(
	  #{examAppctnSeq}
	, #{memSeq}
	, #{examCd}
	, #{issueCd}
	, #{idntfnPhotoFileSeq}
	, #{bsnmNo}
	, 'Y'
	, #{regId}
	, #{regIp}
	, now()
	, #{regId}
	, #{regIp}
	, now()
)

mapper.select:
SELECT /* EBDSqCertiReqMapper.getEducationCompleteLcnsCnnct */ (select GROUP_CONCAT(cd_nm SEPARATOR ' | ') from co_code_mst where lft_val <= (SELECT lft_val FROM co_code_mst WHERE cd = prcs_mst.ctgry_cd) AND rht_val >= (SELECT rht_val FROM co_code_mst WHERE cd = prcs_mst.ctgry_cd) AND dpth < 4 AND dpth > 1) ctgry_cd_nm , prcs_mst.nm , ptcpt_mst.edctn_seq , ptcpt_mst.episd_year , ptcpt_mst.episd_ord , ptcpt_mst.episd_seq , episd_mst.cbsn_cd , (select code_mst.cd_nm from co_code_mst code_mst where code_mst.cd = episd_mst.cbsn_cd and code_mst.cd_id = 'CBSN_CD') cbsn_cd_nm , episd_mst.edctn_strt_dtm , episd_mst.edctn_end_dtm , ptcpt_mst.reg_dtm , ptcpt_mst.cmptn_dtm , prcs_mst.lcns_cnnct_cd , ptcpt_mst.ptcpt_seq FROM ep_edctn_ptcpt_mst ptcpt_mst JOIN ed_edctn_prcs_mst prcs_mst ON prcs_mst.edctn_seq = ptcpt_mst.edctn_seq JOIN ed_edctn_episd_mst episd_mst ON episd_mst.edctn_seq = ptcpt_mst.edctn_seq AND episd_mst.episd_year = ptcpt_mst.episd_year AND episd_mst.episd_ord = ptcpt_mst.episd_ord AND episd_mst.episd_seq = ptcpt_mst.episd_seq
ptcpt_mst.mem_Seq = #{memSeq} AND ptcpt_mst.cmptn_yn = 'Y' and prcs_mst.lcns_cnnct_cd = #{lcnsCnnctCd}
ORDER BY episd_mst.edctn_strt_dtm desc

mapper.insert:
insert into /*EBBEpisdMapper.insertPtcptMst*/ sq_exam_appctn_ptcpt_mst
    (
		exam_appctn_seq
	  , ptcpt_seq
	  , reg_id
	  , reg_ip
	  , reg_dtm
	  , mod_id
	  , mod_ip
	  , mod_dtm
)
values
	(
	  #{examAppctnSeq}
	, #{ptcptSeq}
	, #{regId}
	, #{regIp}
	, now()
	, #{regId}
	, #{regIp}
	, now()
	)

mapper.update:
update /* EBDSqCertiReqMapper.updateCerti */ sq_exam_appctn_mst
set
idntfn_photo_file_seq = #{idntfnPhotoFileSeq}

	, issue_cd = 'EBD_SQ_R'

, mod_ip = #{modIp}
, mod_dtm = now()
WHERE exam_appctn_seq = #{examAppctnSeq}
