mapper.delete:
delete /* EXGExamMapper.deleteExamMst */
from
ex_exam_mst



			exam_seq in

				#{delValueList}



			exam_seq = #{detailsKey}

delete /* EXGExamMapper.deleteExamQstnDtl */
from
ex_exam_qstn_dtl
where


		exam_seq in

			#{delValueList}



		exam_seq = #{detailsKey}



	and qstn_seq = #{qstnSeq}

delete /* EXGExamMapper.deleteExamExmplDtl */
from
ex_exam_exmpl_dtl
where


		exam_seq in

			#{delValueList}



		exam_seq = #{detailsKey}



	and qstn_seq = #{qstnSeq}


	and exmpl_seq = #{exmplSeq}

mapper.select:
select /* EXGExamMapper.getExamEdctnEpisdCnt */ count(*) recp_cnt from ex_exam_mst exam_mst join ed_edctn_episd_mst episd_mst on episd_mst.exam_seq = exam_mst.exam_seq
exam_mst.exam_seq in
#{delValueList}
exam_mst.exam_seq = #{detailsKey}

mapper.insert:
insert /* EXGExamMapper.insertExamMst */ into ex_exam_mst
(
	  exam_seq
	, titl
	, smmry_cntn
	, exps_yn
	, reg_id
	, reg_ip
	, reg_dtm
	, mod_id
	, mod_ip
	, mod_dtm
)
values
	(
	  #{examSeq}
	, #{titl}
	, #{smmryCntn}
	, #{expsYn}
	, #{regId}
	, #{regIp}
	, now()
	, #{regId}
	, #{regIp}
	, now()
	)

insert /* EXGExamMapper.insertExamQstnDtl */ into ex_exam_qstn_dtl
(
	  exam_seq
	, qstn_seq
	, srv_type_cd
	, qstn_nm
	, qstn_ord
	, scord
	, reg_id
	, reg_ip
	, reg_dtm
	, mod_id
	, mod_ip
	, mod_dtm
)
values

	(
	  #{list.examSeq}
	, #{list.qstnSeq}
	, #{list.srvTypeCd}
	, #{list.qstnNm}
	, #{list.qstnOrd}
	, #{list.scord}
	, #{list.regId}
	, #{list.regIp}
	, now()
	, #{list.regId}
	, #{list.regIp}
	, now()
	)

insert /* EXGExamMapper.insertExamExmplDtl */ into ex_exam_exmpl_dtl
(
	  exam_seq
	, qstn_seq
	, exmpl_seq
	, exmpl_nm
	, exmpl_ord
	, cansw_yn
	, reg_id
	, reg_ip
	, reg_dtm
	, mod_id
	, mod_ip
	, mod_dtm
)
values

	(
	  #{list.examSeq}
	, #{list.qstnSeq}
	, #{list.exmplSeq}
	, #{list.exmplNm}
	, #{list.exmplOrd}
	, #{list.canswYn}
	, #{list.regId}
	, #{list.regIp}
	, now()
	, #{list.regId}
	, #{list.regIp}
	, now()
	)

mapper.update:
update /* EXGExamMapper.updateExamMstExpnYn */ ex_exam_mst
	set
	    exps_yn = #{expsYn}
where
	exam_seq = #{detailsKey}

update /* EXGExamMapper.updateExamMst */ ex_exam_mst
set
	 exps_yn = #{expsYn}
    ,titl = #{titl}
    ,smmry_cntn = #{smmryCntn}
    ,mod_id = #{modId}
    ,mod_ip = #{modIp}
    ,mod_dtm = now()
where
	exam_seq = #{detailsKey}

update /* EXGExamMapper.updateExamQstnDtlSequence */ co_seq_mst
set
	NEXT_ID = #{netxId}
where
	TABLE_NAME = #{tableName}
