mapper.select:
select mem_seq , id , name , hp_no , email , reg_dtm , mod_dtm , mem_cd , work_bsnm_no , mod_name , cmpn_nm , size_cd_nm , ctgry_cd_nm , gndr , tel_no , zipcode , bsc_addr , dtl_addr , ntfy_sms_rcv_yn , ntfy_email_rcv_yn from ( select mem.mem_seq , mem.id , mem.name , mem.hp_no , mem.email , mem.reg_dtm , mem.mod_dtm , mem.gndr , mem.mem_cd , mem.work_bsnm_no , mem.ntfy_sms_rcv_yn , mem.ntfy_email_rcv_yn , case when mst.mod_cd ='AD' then (select name from co_adm_mst where id=mst.reg_id) when mst.mod_cd = 'CO' then (select name FROM co_mem_mst where id=mst.reg_id) else null end as mod_name , cmpn_mst.cmpn_nm , (select cd_nm from co_code_mst where cd=cmpn_mst.size_cd ) as size_cd_nm , (select cd_nm from co_code_mst where cd=cmpn_mst.ctgry_cd ) as ctgry_cd_nm , cmpn_mst.ctgry_cd , mem.tel_no , mem.zipcode , mem.bsc_addr , mem.dtl_addr from co_mem_mst mem inner join ( select mem_mod_seq , mem_seq , mod_cd , reg_id from co_mem_mod_log where ( mem_mod_seq , mem_seq) in ( select max(mem_mod_seq) , mem_seq from co_mem_mod_log group by mem_seq ) ) mst on mem.mem_seq = mst.mem_seq left outer join co_mem_cmpn_rel cmpn_rel on mem.mem_seq = cmpn_rel.mem_seq and mem.work_bsnm_no = cmpn_rel.bsnm_no left outer join cp_cmpn_mst cmpn_mst on cmpn_mst.bsnm_no = cmpn_rel.bsnm_no) as ma
order by ma.reg_dtm desc
limit #{firstIndex}, #{recordCountPerPage}

mapper.update:
update co_mem_mst
    set zipcode =#{zipcode}
      , bsc_addr =#{bscAddr}
      , dtl_addr =#{dtlAddr}
      , tel_no =#{telNo}
      , email =#{email}
      , ntfy_sms_rcv_yn=#{ntfySmsRcvYn}
      , ntfy_email_rcv_yn =#{ntfyEmailRcvYn}
      , mod_id =#{regId}
      , mod_ip =#{regIp}
      , mod_dtm = now()
    where id =#{id}
