mapper.select:
SELECT MEM_SEQ /* MPAUserMapper.selectUserList */ , ID , NAME , HP_NO , EMAIL , REG_DTM , MOD_DTM , MEM_CD , WORK_BSNM_NO , MOD_NAME , CMPN_NM , SIZE_CD_NM , SIZE_CD , CTGRY_CD_NM , CTGRY_CD , GNDR , TEL_NO , ZIPCODE , BSC_ADDR , DTL_ADDR , NTFY_SMS_RCV_YN , NTFY_EMAIL_RCV_YN , CMSSR_TYPE_CD , CMSSR_TYPE_CD_NM , CMSSR_CBSN_CD , CMSSR_CBSN_CD_NM , CMSSR_WORK_CD , CMSSR_WORK_CD_NM , CMT_MOD_NAME , CMPN_TEL_NO , mod_id , dept_cd , dept_dtl_nm , dept_cd_nm , pstn_cd_nm , pstn_cd , reg_id , cmssr_mjr_carer_cntn , birth FROM ( SELECT MEM.MEM_SEQ , MEM.ID , MEM.NAME , MEM.HP_NO , MEM.EMAIL , MEM.REG_DTM , MEM.MOD_DTM , MEM.GNDR , MEM.MEM_CD , MEM.WORK_BSNM_NO , MEM.NTFY_SMS_RCV_YN , MEM.NTFY_EMAIL_RCV_YN , CASE WHEN MST.MOD_CD ='AD' THEN (SELECT NAME FROM CO_ADM_MST WHERE ID=MST.REG_ID) WHEN MST.MOD_CD = 'CO' THEN (SELECT NAME FROM CO_MEM_MST WHERE ID=MST.REG_ID) ELSE NULL END AS MOD_NAME , CMPN_MST.CMPN_NM , CASE WHEN CMPN_MST.SIZE_CD != '' THEN (SELECT CD_NM FROM CO_CODE_MST WHERE CD=CMPN_MST.SIZE_CD ) ELSE '' END as SIZE_CD_NM , CASE WHEN CMPN_MST.SIZE_CD != '' THEN (SELECT CD FROM CO_CODE_MST WHERE CD=CMPN_MST.SIZE_CD ) ELSE '' END as SIZE_CD , CASE WHEN CMPN_MST.CTGRY_CD != '' THEN (SELECT CD_NM FROM CO_CODE_MST WHERE CD=CMPN_MST.CTGRY_CD ) ELSE '' END as CTGRY_CD_NM , CMPN_MST.CTGRY_CD , MEM.TEL_NO , MEM.ZIPCODE , MEM.BSC_ADDR , MEM.DTL_ADDR , MEM.CMSSR_TYPE_CD , CASE WHEN MEM.CMSSR_TYPE_CD != '' THEN (SELECT CD_NM FROM CO_CODE_MST WHERE CD=MEM.CMSSR_TYPE_CD ) ELSE '' END as CMSSR_TYPE_CD_NM , MEM.CMSSR_CBSN_CD , CASE WHEN MEM.CMSSR_CBSN_CD != '' THEN (SELECT CD_NM FROM CO_CODE_MST WHERE CD=MEM.CMSSR_CBSN_CD ) ELSE '' END as CMSSR_CBSN_CD_NM , MEM.CMSSR_WORK_CD , CASE WHEN MEM.CMSSR_WORK_CD != '' THEN (SELECT CD_NM FROM CO_CODE_MST WHERE CD=MEM.CMSSR_WORK_CD ) ELSE '' END as CMSSR_WORK_CD_NM , CASE WHEN (SELECT COUNT(*) FROM CO_ADM_MST WHERE ID=MEM.REG_ID) > 0 THEN (SELECT NAME FROM CO_ADM_MST WHERE ID=MEM.REG_ID) WHEN (SELECT COUNT(*) FROM CO_ADM_MST WHERE ID=MEM.REG_ID) = 0 THEN (SELECT NAME FROM CO_MEM_MST WHERE ID=MEM.REG_ID) ELSE NULL END AS CMT_MOD_NAME , MEM.BIRTH , MEM.CMSSR_MPLMN_DT , MEM.CMSSR_RSGNT_DT , MEM.WTHDRW_YN , CMPN_MST.TEL_NO AS CMPN_TEL_NO , mst.reg_id as mod_id , mem.dept_cd , (select cd_nm from co_code_mst where cd=mem.dept_cd ) as dept_cd_nm , mem.dept_dtl_nm , mem.pstn_cd , (select cd_nm from co_code_mst where cd=mem.pstn_cd ) as pstn_cd_nm , mem.reg_id , mem.cmssr_mjr_carer_cntn FROM CO_MEM_MST MEM INNER JOIN ( SELECT MEM_MOD_SEQ , MEM_SEQ , MOD_CD , REG_ID FROM CO_MEM_MOD_LOG WHERE ( MEM_MOD_SEQ , MEM_SEQ) IN ( SELECT MAX(MEM_MOD_SEQ) , MEM_SEQ FROM CO_MEM_MOD_LOG GROUP BY MEM_SEQ ) ) MST ON MEM.MEM_SEQ = MST.MEM_SEQ LEFT OUTER JOIN CO_MEM_CMPN_REL CMPN_REL ON MEM.MEM_SEQ = CMPN_REL.MEM_SEQ AND MEM.WORK_BSNM_NO = CMPN_REL.BSNM_NO LEFT OUTER JOIN CP_CMPN_MST CMPN_MST ON MEM.WORK_BSNM_NO = CMPN_MST.BSNM_NO) AS MA
ORDER BY MA.REG_DTM DESC
LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT mem_seq /* MPAUserMapper.selectUserCompanyInfoList */ , id , name , hp_no , email , reg_dtm , mod_dtm , mem_cd , work_bsnm_no , mod_name , cmpn_nm , size_cd_nm , ctgry_cd_nm , gndr , tel_no , zipcode , bsc_addr , dtl_addr , ntfy_sms_rcv_yn , ntfy_email_rcv_yn FROM (SELECT mem.mem_seq , mem.id , mem.name , mem.hp_no , mem.email , mem.reg_dtm , mem.mod_dtm , mem.gndr , mem.mem_cd , mem.work_bsnm_no , mem.ntfy_sms_rcv_yn , mem.ntfy_email_rcv_yn , CASE WHEN mst.mod_cd = 'AD' THEN (SELECT name FROM co_adm_mst WHERE id = mst.reg_id) WHEN mst.mod_cd = 'CO' THEN (SELECT name FROM co_mem_mst WHERE id = mst.reg_id) ELSE NULL end AS mod_name , cmpn_mst.cmpn_nm , (SELECT cd_nm FROM co_code_mst WHERE cd = cmpn_mst.size_cd) AS size_cd_nm , (SELECT cd_nm FROM co_code_mst WHERE cd = cmpn_mst.ctgry_cd) AS ctgry_cd_nm , cmpn_mst.ctgry_cd , mem.tel_no , mem.zipcode , mem.bsc_addr , mem.dtl_addr FROM co_mem_mst mem INNER JOIN (SELECT mem_mod_seq , mem_seq , mod_cd , reg_id FROM co_mem_mod_log WHERE ( mem_mod_seq, mem_seq ) IN (SELECT Max(mem_mod_seq) , mem_seq FROM co_mem_mod_log GROUP BY mem_seq)) mst ON mem.mem_seq = mst.mem_seq LEFT OUTER JOIN co_mem_cmpn_rel cmpn_rel ON mem.mem_seq = cmpn_rel.mem_seq AND mem.work_bsnm_no = cmpn_rel.bsnm_no LEFT OUTER JOIN cp_cmpn_mst cmpn_mst ON mem.work_bsnm_no = cmpn_mst.bsnm_no) AS ma
order by ma.reg_dtm desc
limit #{firstIndex}, #{recordCountPerPage}

sql.where:
and ma.mem_cd = #{memCd} and ma.wthdrw_yn = #{wthdrwYn}
and ma.reg_dtm >= str_to_date(#{strtDt}, '%Y-%m-%d')
and ma.reg_dtm < date_add(str_to_date(#{endDt}, '%Y-%m-%d'), interval 1 day)
and ma.mod_dtm >= str_to_date(#{strtDt}, '%Y-%m-%d')
and ma.mod_dtm < date_add(str_to_date(#{endDt}, '%Y-%m-%d'), interval 1 day)
and (
ma.ctgry_cd = #{ctgryCdList}
)
and (
ma.cmssr_type_cd = #{cmssrTypeList}
)
and (
ma.cmssr_work_cd = #{cmssrWorkList}
)
/* 회원검색 레이어일 경우 특정 부품사의 회원들만 조회되도록 */
and ma.work_bsnm_no = #{workBsnmNo}
/* 회원검색시 1차 2차 필요 할때 */ and ma.ctgry_cd in ('COMPANY01001' , 'COMPANY01002')
/* 회원검색시 1차 2차 필요 할때 */ and ma.cmssr_work_cd in ('MEM_CD04001')
and ma.id like concat('%', #{q}, '%')
and ma.name like concat('%', #{q}, '%')
and ma.hp_no like concat('%', #{q}, '%')
and ma.email like concat('%', #{q}, '%')
and (ma.mod_name like concat('%', #{q}, '%') )
and (ma.cmpn_nm like concat('%', #{q}, '%') )
and (ma.work_bsnm_no like concat('%', #{q}, '%') )
and (ma.cmt_mod_name like concat('%', #{q}, '%') )
and ( ma.id like concat('%', #{q}, '%') or ma.name like concat('%', #{q}, '%') or ma.hp_no like concat('%', #{q}, '%') or ma.email like concat('%', #{q}, '%') or ma.mod_name like concat('%', #{q}, '%') or ma.cmpn_nm like concat('%', #{q}, '%') or ma.work_bsnm_no like concat('%', #{q}, '%') or ma.cmt_mod_name like concat('%', #{q}, '%') )
and ma.mem_seq not in (select bfre_mem_seq from cp_Cnstg_appctn_trnsf_dtl where cnstg_seq = #{cnstgSeq})

mapper.select:
select count(*) /* MPAUserMapper.selectDupEmail */ from co_mem_mst where 1=1
and mem_seq not in(#{detailsKey})
and email=#{email}

select count(*)  /* MPAUserMapper.selectDupId */
 from co_mem_mst
  where id =#{id}

