mapper.select:
select m1.cnstg_seq -- 순번 /*MPBMemberPartsSocietyMapper.selectBusList*/ ,m1.bsn_year -- 사업년도 , (select cd_nm from co_code_mst where cd=m1.cnstg_cd ) as cnstg_cd_nm -- 사업구분 , (SELECT CD_NM FROM CO_CODE_MST WHERE CD = (CASE WHEN m1.RSUME_STTS_CD = 'MNGTECH_STATUS_06' AND ((SELECT IFNULL(GUIDE_BGN_DT, CURDATE() + INTERVAL 1 DAY) FROM cp_cnstg_rsume_mst WHERE CNSTG_SEQ = m1.CNSTG_SEQ ) <= CURDATE() AND (SELECT IFNULL(guide_kickf_dt, CURDATE() + INTERVAL 1 DAY) FROM cp_cnstg_rsume_mst WHERE CNSTG_SEQ = m1.CNSTG_SEQ ) > CURDATE()) then 'MNGTECH_STATUS_08' WHEN (m1.RSUME_STTS_CD = 'MNGTECH_STATUS_06' OR m1.RSUME_STTS_CD = 'MNGTECH_STATUS_08' ) AND ((SELECT IFNULL(guide_kickf_dt, CURDATE() + INTERVAL 1 DAY) FROM cp_cnstg_rsume_mst WHERE CNSTG_SEQ = m1.CNSTG_SEQ ) <= CURDATE()) then 'MNGTECH_STATUS_09' ELSE m1.RSUME_STTS_CD END) AND CD_ID = 'MNGTECH_STATUS') status_nm -- 진행상태 , m2.cmpn_nm-- 부품사명 , (select cd_nm from co_code_mst where cd=m2.ctgry_cd ) as ctgry_cd_nm -- 구분 , (select cd_nm from co_code_mst where cd=m1.cmpn_size_cd ) as cmpn_size_cd_nm-- 규모 , m1.appctn_bsnm_no-- 사업자등록번호 , (select cd_nm from co_code_mst where cd=m1.appctn_fld_cd ) as appctn_fld_cd_nm-- 신청분야/업종 , CONCAT((select cd_nm from co_code_mst where cd=m1.first_rgns_cd ),' ',(select cd_nm from co_code_mst where cd=m1.scnd_rgns_cd )) as rgns_cd_nm-- 신청 소재지 , m4.name-- 담당위원 , m1.appctn_dt-- 신청일 , m3.guide_bgn_dt-- 지도착수일 , m3.guide_pscnd_dt-- 지도완료일 , m3.flty_impvm_rate -- 개선률 from cp_cnstg_appctn_mst m1 inner join cp_cmpn_mst m2 on m1.appctn_bsnm_no = m2.bsnm_no left outer join cp_cnstg_rsume_mst m3 on m1.cnstg_seq = m3.cnstg_seq left outer join co_mem_mst m4 on m3.cmssr_seq = m4.mem_seq where 1 = 1
and appctn_mem_seq=#{detailsKey}
and m3.cmssr_seq=#{detailsKey}
order by cnstg_seq desc limit #{firstIndex}, #{recordCountPerPage}

select count(*) /*MPBMemberPartsSocietyMapper.selectBusListCnt */ from cp_cnstg_appctn_mst m1 inner join cp_cmpn_mst m2 on m1.appctn_bsnm_no = m2.bsnm_no left outer join cp_cnstg_rsume_mst m3 on m1.cnstg_seq = m3.cnstg_seq left outer join co_mem_mst m4 on m3.cmssr_seq = m4.mem_seq where 1 = 1
and appctn_mem_seq=#{detailsKey}
and m3.cmssr_seq=#{detailsKey}

select m1.episd_seq as san_ctn_seq /*MPBMemberPartsSocietyMapper.selectSanList */ , m1.year-- 사업년도 , m1.episd-- 회차 , case when (select cd_nm from co_code_mst where cd=m1.bsn_cd ) in ('미래차다각화', '사업재편지원', '글로벌스탠다드', '청년인재양성', '보안환경구축', '안전설비구축', '탄소배출저감', '스마트공장구축', '기획전용사업') then '경쟁력향상지원' when (select cd_nm from co_code_mst where cd=m1.bsn_cd ) in ('시험계측장비', '검교정', '공급망안정화기금') then '자금지원' when (select cd_nm from co_code_mst where cd=m1.bsn_cd ) in ('자동차부품산업대상', '미래차공모전', '역대 수상자') then '포상지원' else '설문조사' end as type_cd_nm , (select cd_nm from co_code_mst where cd =(select rsume_stts_cd from cx_appctn_rsume_dtl where appctn_seq = m2.appctn_seq order by rsume_ord desc limit 1)) as status , (select cd_nm from co_code_mst where cd=m1.bsn_cd ) as bsn_cd_nm-- 과제명 , (select menu_nm from co_menu_mst cmm where menu_seq = (select user_menu_seq from cx_mst where bsn_cd = m1.bsn_cd) ) as menu_cd_nm -- 사업구분 , m3.cmpn_nm -- 부품사명 , (select cd_nm from co_code_mst where cd=m3.ctgry_cd ) as ctgry_cd_nm -- 구분 , (select cd_nm from co_code_mst where cd=m3.size_cd ) as cmpn_size_cd_nm -- 규모 , m2.appctn_bsnm_no-- 사업자등록번호 , (select name from co_mem_mst where mem_seq=m2.pic_cmssr_seq) as name -- 담당위원 , m2.reg_dtm-- 신청일 , m1.bsn_strt_dtm , m1.bsn_end_dtm from cx_episd_mst m1 inner join cx_appctn_mst m2 on m1.episd_seq = m2.episd_seq inner join cp_cmpn_mst m3 on m2.appctn_bsnm_no = m3.bsnm_no where 1 = 1
and m2.mem_seq =#{detailsKey}
and m2.pic_cmssr_seq=#{detailsKey}
order by m1.episd_seq desc limit #{firstIndex}, #{recordCountPerPage}

