    , FFLTN_NM
    , DEPT_NM
    , PSTN_NM
    , TEL_NO
    , HP_NO
    , EMAIL
    , SPCL_CNTN
    , REG_ID
    , REG_IP
    , REG_DTM
)
VALUES
    (
    #{isttrSeq}
    , #{name}
    , #{ffltnNm}
    , #{deptNm}
    , #{pstnNm}
    , #{telNo}
    , #{hpNo}
    , #{email}
    , #{spclCntn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE /* MPCLecturerMapper.updateLecturer */ CP_ISTTR_MST
SET
    NAME = #{name}
    , FFLTN_NM = #{ffltnNm}
    , DEPT_NM = #{deptNm}
    , PSTN_NM = #{pstnNm}
    , TEL_NO = #{telNo}
    , HP_NO = #{hpNo}
    , EMAIL = #{email}
    , SPCL_CNTN = #{spclCntn}
    , MOD_ID = #{modId}
    , MOD_IP = #{modIp}
    , MOD_DTM = NOW()
WHERE 1=1
  AND ISTTR_SEQ = #{isttrSeq}

mapper.delete:
DELETE /* MPCLecturerMapper.deleteLecturer */
FROM
CP_ISTTR_MST



            ISTTR_SEQ IN

