mapper.insert:
insert into co_mem_mst
(   mem_seq 				-- 회원순번
, mem_cd  				-- 회원코드
, id   					-- id
, name  					-- 이름
, pwd  					-- 비밀번호
, birth 					-- 생년월일
, hp_no 					-- 휴대번호
, email  					-- 이메일
, cmssr_type_cd 			-- 위원구분 코드
, cmssr_cbsn_cd 			-- 업종/분야
, cmssr_work_cd 			-- 재직코드
, cmssr_mplmn_dt 			-- 입사일

, cmssr_rsgnt_dt  		-- 퇴사일

, cmssr_photo_file_seq  	-- 파일순번
, cmssr_mjr_carer_cntn  	-- 경력 내용
, cmssr_cnstg_fld_cntn
, cmssr_mjr_carer_exps_yn -- 노출 여
, ntfy_sms_rcv_yn  		-- sms 여부
, ntfy_sms_rcv_chng_dtm
, ntfy_email_rcv_yn  		-- email 여부
, ntfy_email_rcv_chng_dtm
, wthdrw_yn   			-- 탈퇴여부
, lgn_fail_cnt
, reg_id 					-- 등록id
, reg_ip  				-- 등록ip
, reg_dtm  				-- 등록일
, mod_id  				-- 수정id
, mod_ip  				-- 수정ip
, mod_dtm	 				-- 수정일
, pwd_chn
, chng_xtnsn_cnt
, tmp_pwd_yn
, pwd_chng_dtm
)
values(
        #{memSeq}
      , #{memCd}
