      , #{cmssrCnstgFldCntn}
      , #{cmssrMjrCarerCntn}
      , #{cmssrMjrCarerExpsYn}
      , 'N'
      , now()
      , 'N'
      , now()
      , 'Y'
      , '0'
      , #{regId}
      , #{regIp}
      , now()
      , #{regId}
      , #{regIp}
      , now()
      , '0'
