      , now()
      )

mapper.update:
update co_mem_mst
 set wthdrw_yn = 'N'



                 mem_seq in

                     #{delValueList}



                 adm_seq = #{detailsKey}

mapper.select:
select (select cd_nm from co_code_mst where cd=m1.atndc_cd ) as atndcCdNm -- 근태옵션 , atndc_cd , guide_part_cmpn_1 , guide_part_cmpn_2 , rgns_1 , rgns_2 , etc_bsntrp-- 기타출장 , etc-- 기타 , m1.reg_dtm-- 근태체크일시 , name from co_cmssr_atndc_mst m1 inner join co_mem_mst m2 on m1.mem_seq = m2.mem_seq where 1=1
and m1.mem_seq =#{detailsKey}
and year=#{year} and mnth=#{mnth}
and dd = #{dd}
order by reg_dtm desc
limit #{firstIndex}, #{recordCountPerPage}

select count(*)
    from co_cmssr_atndc_mst m1
     inner join co_mem_mst m2
      on m1.mem_seq = m2.mem_seq
     where 1=1

     and m1.mem_seq =#{detailsKey}

      and year=#{year}
      and mnth=#{mnth}

        and dd = #{dd}

select count(*) from(
SELECT count(*)
 FROM co_cmssr_atndc_mst m1
  inner join co_mem_mst m2
   on m1.mem_seq = m2.mem_seq
    where  year=#{year}
     and mnth=#{mnth}
      and m2.wthdrw_yn=#{wthdrwYn}
      group by m1.mem_seq ) as a

