mapper.select:
SELECT /* MPEPartsCompanyMapper.selectPartsCompanyList */ BSNM_NO , RPRSNT_NM , CMPN_NM , CMPN_CD , CTGRY_CD , (SELECT CD_NM FROM CO_CODE_MST WHERE CD_ID = 'COMPANY_TYPE' AND CD = A.CTGRY_CD) AS CTGRY_NM , SIZE_CD , (SELECT CD_NM FROM CO_CODE_MST WHERE CD_ID = 'COMPANY_TYPE' AND CD = A.SIZE_CD) AS SIZE_NM , BSNM_NO , BSC_ADDR , DTL_ADDR , SLS_PMT , MPLE_CNT , TEL_NO , REG_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME , REG_IP , REG_DTM , MOD_ID , (SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME , MOD_IP , MOD_DTM , #{srchDate} AS 'srchDate' , #{dStrDt} AS 'dStrDt' , #{dEndDt} AS 'dEndDt' FROM CP_CMPN_MST A
ORDER BY REG_DTM DESC
LIMIT #{firstIndex}, #{recordCountPerPage}

