mapper.insert:

    SELECT IFNULL(MAX(EXPS_ORD), 0) + 1 FROM SM_MAIN_POPUP_MST

INSERT /* SMCMnPopMapper.insertMnPop */ INTO SM_MAIN_POPUP_MST
(
POPUP_SEQ
, MD_CD
, EXPS_STRT_DTM
, EXPS_END_DTM
, TITL
, TAG_CD
, FILE_SEQ
, CNTN
, URL_URL
, WNPP_YN
, ODTM_YN
, EXPS_ORD
, EXPS_YN
, REG_ID
, REG_IP
, REG_DTM
)
VALUES
(
#{popupSeq}
, #{mdCd}


        , NULL
        , NULL


        , STR_TO_DATE(CONCAT(#{expsStrtDtm}, ' ', #{ptupStrtHh}, ':', #{ptupStrtMi}), '%Y-%m-%d %H:%i')
        , STR_TO_DATE(CONCAT(#{expsEndDtm}, ' ', #{ptupEndHh}, ':', #{ptupEndMi}), '%Y-%m-%d %H:%i')


, #{titl}
, #{tagCd}
, #{fileSeq}
, #{cntn}
, #{urlUrl}
, #{wnppYn}
, #{odtmYn}
, #{expsOrd}
, #{expsYn}
, #{regId}
, #{regIp}
, NOW()
)

mapper.delete:
DELETE /* SMCMnPopMapper.deleteMnPop */
FROM
SM_MAIN_POPUP_MST



            POPUP_SEQ IN

                #{delValueList}



            POPUP_SEQ = #{popupSeq}


    AND MD_CD = #{mdCd}

mapper.update:
UPDATE /* SMCMnPopMapper.updateMnPop */ SM_MAIN_POPUP_MST
SET
TITL = #{titl}


        , EXPS_STRT_DTM = NULL
        , EXPS_END_DTM = NULL


        , EXPS_STRT_DTM = STR_TO_DATE(CONCAT(#{expsStrtDtm}, ' ', #{ptupStrtHh}, ':', #{ptupStrtMi}), '%Y-%m-%d %H:%i')
        , EXPS_END_DTM = STR_TO_DATE(CONCAT(#{expsEndDtm}, ' ', #{ptupEndHh}, ':', #{ptupEndMi}), '%Y-%m-%d %H:%i')


, ODTM_YN = #{odtmYn}
, TAG_CD = #{tagCd}
, FILE_SEQ = #{fileSeq}
, CNTN = #{cntn}
, URL_URL = #{urlUrl}
, WNPP_YN = #{wnppYn}
, EXPS_YN = #{expsYn}
, MOD_ID = #{modId}
, MOD_IP = #{modIp}
, MOD_DTM = NOW()
  WHERE 1=1
AND POPUP_SEQ = #{popupSeq}
AND MD_CD = #{mdCd}

UPDATE /* SMCMnPopMapper.updateUseYn */ SM_MAIN_POPUP_MST
SET
EXPS_YN = 'N'
, MOD_ID = #{modId}
, MOD_IP = #{modIp}
, MOD_DTM = NOW()



            POPUP_SEQ IN

                #{delValueList}



            POPUP_SEQ = #{popupSeq}


    AND MD_CD = #{mdCd}

UPDATE /* SMCMnPopMapper.updateOrder */ SM_MAIN_POPUP_MST
SET
    EXPS_ORD = #{expsOrd}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND POPUP_SEQ = #{popupSeq}
  AND MD_CD = #{mdCd}
