mapper.insert:

    SELECT IFNULL(MAX(ORD), 0) + 1 FROM SM_POPUP_MST

INSERT /* SMCPopMapper.insertMnPop */ INTO SM_POPUP_MST
(
SEQ
, LANG_CD
, DVC_CD
, STRT_DTM
, END_DTM
, TITL
, FILE_SEQ
, CNTS
, LINK_URL
, NEW_WNDW_YN
, ODTM_YN
, ORD
, USE_YN
, REG_ID
, REG_IP
, REG_DTM
, TYPE_CD
)
VALUES
(
#{detailsKey}
, #{langCd}
, #{dvcCd}


        , NULL
        , NULL


        , STR_TO_DATE(CONCAT(#{strtDtm}, ' ', #{ptupStrtHh}, ':', #{ptupStrtMi}), '%Y-%m-%d %H:%i')
        , STR_TO_DATE(CONCAT(#{endDtm}, ' ', #{ptupEndHh}, ':', #{ptupEndMi}), '%Y-%m-%d %H:%i')


, #{titl}
, #{fileSeq}
, #{cnts}
, #{linkUrl}
, #{newWndwYn}
, #{odtmYn}
, #{ord}
, #{useYn}
, #{regId}
, #{regIp}
, NOW()
, #{typeCd}
)

mapper.delete:
DELETE /* SMCPopMapper.deleteMnPop */
FROM
SM_POPUP_MST



            SEQ IN

                #{delValueList}



            SEQ = #{seq}


    AND DVC_CD = #{dvcCd}

mapper.update:
UPDATE /* SMCPopMapper.updateMnPop */ SM_POPUP_MST
SET
TITL = #{titl}


        , STRT_DTM = NULL
        , END_DTM = NULL


        , STRT_DTM = STR_TO_DATE(CONCAT(#{strtDtm}, ' ', #{ptupStrtHh}, ':', #{ptupStrtMi}), '%Y-%m-%d %H:%i')
        , END_DTM = STR_TO_DATE(CONCAT(#{endDtm}, ' ', #{ptupEndHh}, ':', #{ptupEndMi}), '%Y-%m-%d %H:%i')


, ODTM_YN = #{odtmYn}
, FILE_SEQ = #{fileSeq}
, CNTS = #{cnts}
, LINK_URL = #{linkUrl}
, NEW_WNDW_YN = #{newWndwYn}
, USE_YN = #{useYn}
, MOD_ID = #{modId}
, MOD_IP = #{modIp}
, MOD_DTM = NOW()
, TYPE_CD = #{typeCd}
WHERE 1=1
AND SEQ = #{seq}
AND DVC_CD = #{dvcCd}

UPDATE /* SMCPopMapper.updateUseYn */ SM_POPUP_MST
SET
USE_YN = 'N'
, MOD_ID = #{modId}
, MOD_IP = #{modIp}
, MOD_DTM = NOW()



            SEQ IN

                #{delValueList}



            SEQ = #{seq}


    AND DVC_CD = #{dvcCd}

UPDATE /* SMCPopMapper.updateOrder */ SM_POPUP_MST
SET
    ORD = #{ord}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND SEQ = #{seq}
  AND DVC_CD = #{dvcCd}
