mapper.insert:
INSERT /* SMDAPsnIfMapper.insertPsnIf */ INTO SM_PSNIF_MST
(
PSNIF_SEQ
, TITL
, CNTN
, EXPS_YN
, REG_ID
, REG_IP
, REG_DTM
)
VALUES
    (
    #{psnifSeq}
    , #{titl}
    , #{cntn}
    , #{expsYn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE /* SMDAPsnIfMapper.updatePsnIf */ SM_PSNIF_MST
SET
    TITL = #{titl}
  , CNTN = #{cntn}
  , EXPS_YN = #{expsYn}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND PSNIF_SEQ = #{detailsKey}

mapper.delete:
DELETE /* SMDAPsnIfMapper.deletePsnIf */
FROM
SM_PSNIF_MST



            PSNIF_SEQ IN

                #{item}



            PSNIF_SEQ = #{detailsKey}
