mapper.insert:
INSERT /* SMDBTmncsMapper.insertTmncs */ INTO SM_TMNCS_MST
(
 TMNCS_SEQ
, CNTN
, REG_ID
, REG_IP
, REG_DTM
)
VALUES
    (
    #{detailsKey}
    , #{cntn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE /* SMDBTmncsMapper.updateTmncs */ SM_TMNCS_MST
SET
  CNTN = #{cntn}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND TMNCS_SEQ = #{detailsKey}
