mapper.insert:
INSERT /* SMDCMemTrmsMapper.insertMemTrms */ INTO SM_MEM_TRMS_MST
(
    MEM_TRMS_SEQ
, TMNCS_CNTN
, PSNIF_CNTN
, OFFER_CNTN
, MKT_RCV_AGMNT_CNTN
, REG_ID
, REG_IP
, REG_DTM
)
VALUES
    (
        #{memTrmsSeq}
    , #{tmncsCntn}
    , #{psnifCntn}
    , #{offerCntn}
    , #{offerCntn}
    , #{regId}
    , #{regIp}
    , NOW()
    )

mapper.update:
UPDATE /* SMDCMemTrmsMapper.updateMemTrms */ SM_MEM_TRMS_MST
SET
    TMNCS_CNTN = #{tmncsCntn}
  , PSNIF_CNTN = #{psnifCntn}
  , OFFER_CNTN = #{offerCntn}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND MEM_TRMS_SEQ = #{detailsKey}
