mapper.delete:
delete /* SVASurveyMapper.deleteExamMst */ sv_mst , sv_qstn_dtl , sv_exmpl_dtl from sv_mst inner join sv_qstn_dtl on sv_mst.srv_seq = sv_qstn_dtl.srv_seq inner join sv_exmpl_dtl on sv_qstn_dtl.qstn_seq = sv_exmpl_dtl.qstn_seq
