mapper.select:
SELECT /* WBAManagementMapper.selecApplyCnt*/ COUNT(*)
FROM cx_appctn_mst a
JOIN cx_episd_mst b ON a.episd_seq = b.episd_Seq AND b.bsn_cd = #{bsnCd}

mapper.insert:
INSERT /* WBAManagementMapper.insertManagementMst */ into cx_mst
