mapper.insert:
INSERT /* WBBARoundMapper.insertRound */ INTO CX_EPISD_MST
(
    EPISD_SEQ
,BSN_CD
,YEAR
,EPISD
,ACCS_STRT_DTM
,ACCS_END_DTM
,BSN_STRT_DTM
,BSN_END_DTM
,EXPS_YN
,REG_ID
,REG_IP
,REG_DTM
)
    VALUE
    (
    #{episdSeq}
    ,#{bsnCd}
    ,#{year}
    ,#{episd}
    ,#{accsStrtDtm}
    ,#{accsEndDtm}
    ,#{bsnStrtDtm}
    ,#{bsnEndDtm}
    ,#{expsYn}
    ,#{regId}
    ,#{regIp}
    ,NOW()
    )

mapper.update:
UPDATE CX_EPISD_MST SET /* WBBARoundMapper.updateRound  */
    YEAR = #{year}
                      ,EPISD =  #{episd}
                      ,ACCS_STRT_DTM = #{accsStrtDtm}
                      ,ACCS_END_DTM = #{accsEndDtm}
                      ,BSN_STRT_DTM = #{bsnStrtDtm}
                      ,BSN_END_DTM = #{bsnEndDtm}
                      ,EXPS_YN = #{expsYn}
                      ,MOD_ID = #{modId}
                      ,MOD_IP = #{modIp}
                      ,MOD_DTM = NOW()
WHERE EPISD_SEQ = #{detailsKey}

mapper.delete:
DELETE /* WBBARoundMapper.deleteRound */
FROM CX_EPISD_MST
WHERE EPISD_SEQ = #{detailsKey}
