mapper.insert:
INSERT /* WBDASafetyListMapper.insertCarbon */ INTO CX_EPISD_MST
(
    EPISD_SEQ
,BSN_CD
,YEAR
,EPISD
,ACCS_STRT_DTM
,ACCS_END_DTM
,BSN_STRT_DTM
,BSN_END_DTM
,EXPS_YN
,REG_ID
,REG_IP
,REG_DTM
)
    VALUE
    (
    #{episdSeq}
    ,#{bsnCd}
    ,#{year}
    ,#{episd}
    ,#{accsStrtDtm}
    ,#{accsEndDtm}
    ,#{bsnStrtDtm}
    ,#{bsnEndDtm}
    ,#{expsYn}
    ,#{regId}
    ,#{regIp}
    ,NOW()
    )

INSERT /* WBDASafetyListMapper.insertGiveList */ INTO CX_EPISD_GIVE_DTL
(
    GIVE_SEQ
,EPISD_SEQ
,GIVE_ORD
,STRT_DT
,END_DT
,REG_ID
,REG_IP
,REG_DTM
)
VALUES
    (
    #{giveSeq}
    ,#{episdSeq}
    ,#{giveOrd}
    ,#{strtDt}
    ,#{endDt}
    ,#{regId}
    ,#{regIp}
    ,#{regDtm}
    )

INSERT /* WBDASafetyListMapper.insertGiveList */ INTO CX_EPISD_GIVE_DTL
(
    GIVE_SEQ
,EPISD_SEQ
,GIVE_ORD
,STRT_DT
,END_DT
,REG_ID
,REG_IP
,REG_DTM
)
VALUES
    (
        #{giveSeq}
    ,#{detailsKey}
    ,#{giveOrd}
    ,#{strtDt}
    ,#{endDt}
    ,#{regId}
    ,#{regIp}
    ,#{regDtm}
    )

mapper.update:
UPDATE CX_EPISD_MST SET /* WBDASafetyListMapper.updateCarbon  */
      YEAR = #{year}
      ,EPISD =  #{episd}
      ,ACCS_STRT_DTM = #{accsStrtDtm}
      ,ACCS_END_DTM = #{accsEndDtm}
      ,BSN_STRT_DTM = #{bsnStrtDtm}
      ,BSN_END_DTM = #{bsnEndDtm}
      ,EXPS_YN = #{expsYn}
      ,MOD_ID = #{modId}
      ,MOD_IP = #{modIp}
      ,MOD_DTM = NOW()
WHERE EPISD_SEQ = #{detailsKey}

UPDATE CX_EPISD_MST SET /* WBDASafetyListMapper.updateExpsYnCarbon  */
        EXPS_YN = #{expsYn}
        ,MOD_ID = #{modId}
        ,MOD_IP = #{modIp}
        ,MOD_DTM = NOW()
WHERE EPISD_SEQ = #{detailsKey}

mapper.delete:
DELETE /* WBDASafetyListMapper.deleteGiveList */
FROM CX_EPISD_GIVE_DTL
WHERE EPISD_SEQ = #{detailsKey}

DELETE /* WBDASafetyListMapper.deleteCarbon */
FROM CX_EPISD_MST
WHERE EPISD_SEQ = #{detailsKey}

mapper.select:
SELECT /* WBDASafetyListMapper.getAppctnCnt*/ COUNT(*)
FROM CX_APPCTN_MST



            EPISD_SEQ in

                #{delValueList}



            EPISD_SEQ = #{detailsKey}

mapper.delete:
DELETE /* WBDASafetyListMapper.carbonDeleteList */
FROM CX_EPISD_MST



            EPISD_SEQ in

                #{delValueList}



            EPISD_SEQ = #{detailsKey}

mapper.select:
SELECT COUNT(*)
FROM CX_EPISD_MST
WHERE BSN_CD = 'BSN04'
AND YEAR = #{year}
AND EPISD = #{episd}

    AND EPISD_SEQ != #{detailsKey}
