mapper.select:
SELECT COUNT(*) /* WBFASmartRoundMapper.selectRoundCheck */ FROM CX_EPISD_MST
WHERE bsn_cd = #{bsnCd}
AND year = #{year} AND episd = #{episd}

mapper.insert:
INSERT /* WBFASmartRoundMapper.insertRound */ INTO CX_EPISD_MST
(
    EPISD_SEQ
,BSN_CD
,YEAR
,EPISD
,ACCS_STRT_DTM
,ACCS_END_DTM
,BSN_STRT_DTM
,BSN_END_DTM
,EXPS_YN
,REG_ID
,REG_IP
,REG_DTM
)
    VALUE
    (
    #{episdSeq}
    ,#{bsnCd}
    ,#{year}
    ,#{episd}
    ,#{accsStrtDtm}
    ,#{accsEndDtm}
    ,#{bsnStrtDtm}
    ,#{bsnEndDtm}
    ,#{expsYn}
    ,#{regId}
    ,#{regIp}
    ,NOW()
    )

INSERT /* WBFASmartRoundMapper.insertGiveList */ INTO CX_EPISD_GIVE_DTL
(
    GIVE_SEQ
,EPISD_SEQ
,GIVE_ORD
,STRT_DT
,END_DT
,REG_ID
,REG_IP
,REG_DTM
)
VALUES
    (
        #{giveSeq}
    ,#{episdSeq}
    ,#{giveOrd}
    ,#{strtDt}
    ,#{endDt}
    ,#{regId}
    ,#{regIp}
    ,#{regDtm}
    )

INSERT /* WBFASmartRoundMapper.insertBsnOptnList */ INTO cx_episd_bsn_optn_dtl
(
    bsn_optn_seq
    ,episd_seq
    ,optn_cd
    ,optn_nm
    ,optn_ord
    ,reg_id
    ,reg_ip
    ,reg_dtm
)
VALUES
    (
    #{bsnOptnSeq}
    ,#{episdSeq}
    ,#{optnCd}
    ,#{optnNm}
    ,#{optnOrd}
    ,#{regId}
    ,#{regIp}
    ,#{regDtm}
    )

mapper.update:
SELECT COUNT(*)
FROM cx_appctn_mst
WHERE episd_seq  = #{detailsKey}

UPDATE CX_EPISD_MST SET /* WBFASmartRoundMapper.updateRound  */
    YEAR = #{year}
                      ,EPISD =  #{episd}
                      ,ACCS_STRT_DTM = #{accsStrtDtm}
                      ,ACCS_END_DTM = #{accsEndDtm}
                      ,BSN_STRT_DTM = #{bsnStrtDtm}
                      ,BSN_END_DTM = #{bsnEndDtm}
                      ,EXPS_YN = #{expsYn}
                      ,MOD_ID = #{modId}
                      ,MOD_IP = #{modIp}
                      ,MOD_DTM = NOW()
WHERE EPISD_SEQ = #{detailsKey}

