mapper.select:
SELECT cbsn_seq, nm, score, YEAR(year) as year, crtfn_cmpn_nm
FROM cp_cmpn_cbsn_dtl
WHERE bsnm_no = #{bsnmNo}
ORDER BY cbsn_seq;

SELECT rsume_seq, rsume_ord, file_cd, file_seq /*WBFBRegisterCompanyMapper.getRsumeTaskDtlFileList*/
FROM cx_appctn_rsume_file_dtl
WHERE rsume_seq = #{rsumeSeq}

SELECT DISTINCT episd
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
ORDER BY episd_seq ASC

SELECT bsn_optn_seq, optn_cd, optn_nm, optn_ord
FROM cx_episd_bsn_optn_dtl
WHERE episd_seq = #{episdSeq}
AND optn_cd = #{optnCd}
ORDER BY optn_cd, optn_ord;

SELECT episd_seq
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
AND episd = #{episd}

