mapper.select:
SELECT /* WBIBSupplyCompanyMapper.getOptEpisdList */
    DISTINCT episd
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
ORDER BY episd_seq DESC

SELECT /* WBIBSupplyCompanyMapper.getEpisdSeq */
       episd_seq
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
AND episd = #{episd}

mapper.update:
UPDATE /* WBIBSupplyCompanyMapper.updCmpnCbsnDtl */ CP_CMPN_CBSN_DTL
SET
    BSNM_NO = #{bsnmNo}
  , NM = #{nm}
  , SCORE = #{score}
  , YEAR = #{year}
  , CRTFN_CMPN_NM = #{crtfnCmpnNm}
  , MOD_ID = #{modId}
  , MOD_IP = #{modIp}
  , MOD_DTM = NOW()
WHERE 1=1
  AND CBSN_SEQ = #{cbsnSeq}

mapper.delete:
DELETE /* WBIBSupplyCompanyMapper.deleteApplyMst */ FROM CX_APPCTN_MST
APPCTN_SEQ IN
#{item}

DELETE /* WBBBCompanyMapper.deleteApplyDtl */ FROM CX_APPCTN_RSUME_DTL
APPCTN_SEQ IN
#{item}

DELETE /* WBIBSupplyCompanyMapper.deleteTrans */ FROM CX_APPCTN_TRNSF_DTL
APPCTN_SEQ IN
#{item}

mapper.insert:
INSERT /* WBIBSupplyCompanyMapper.insertChangeLog */ INTO CX_APPCTN_TRNSF_DTL
(
    TRNSF_SEQ
, APPCTN_SEQ
, BFRE_MEM_SEQ
, AFTR_MEM_SEQ
, REG_ID
, REG_IP
, REG_DTM
)
    VALUE
    (
    #{trnsfSeq}
    ,#{appctnSeq}
    ,#{bfreMemSeq}
    ,#{aftrMemSeq}
    ,#{regId}
    ,#{regIp}
    ,NOW()
    )
