mapper.update:
UPDATE CX_EPISD_MST SET /* WBJARoundListMapper.updateRound  */
    YEAR = #{year}
      ,EPISD =  #{episd}
      ,ACCS_STRT_DTM = #{accsStrtDtm}
      ,ACCS_END_DTM = #{accsEndDtm}
      ,BSN_STRT_DTM = #{bsnStrtDtm}
      ,BSN_END_DTM = #{bsnEndDtm}
      ,ADD_NTFY_CNTN = #{addNtfyCntn}
      ,PLACE_SEQ = #{placeSeq}
      ,EXPS_YN = #{expsYn}
      ,MOD_ID = #{modId}
      ,MOD_IP = #{modIp}
      ,MOD_DTM = NOW()
WHERE EPISD_SEQ = #{detailsKey}

