mapper.select:
SELECT /*WBKBRegisterMapper.getRegisterList*/ DISTINCT YEAR(A.year) as year, A.episd, B.appctn_seq, B.sbrdn_bsnm_no, B.reg_id, B.reg_dtm , B.mod_id, (SELECT cd_nm FROM co_code_mst WHERE cd = D.theme_cd) as theme_cd_nm, C.rsume_seq, C.max_rsume_ord as rsume_ord, C.appctn_stts_cd, C.mng_stts_cd, (SELECT cd_nm FROM co_code_mst WHERE cd = D.ptcpt_type) as ptcpt_type_nm, D.wdcrm_cd, B.mod_id, B.mod_dtm FROM ( SELECT * FROM cx_episd_mst WHERE bsn_cd = 'INQ07011' ) A INNER JOIN cx_appctn_mst B ON B.episd_seq = A.episd_seq INNER JOIN( SELECT appctn_seq, MAX(rsume_ord) AS max_rsume_ord, rsume_seq, appctn_stts_cd , mng_stts_cd FROM cx_appctn_rsume_dtl GROUP BY appctn_seq )C on C.appctn_seq = B.appctn_seq INNER JOIN cx_appctn_rsume_prize_car_dtl D on D.rsume_seq = C.rsume_seq
ORDER BY YEAR DESC, APPCTN_SEQ DESC
LIMIT #{firstIndex}, #{recordCountPerPage};

SELECT /*WBKBRegisterMapper.selectFutureCarFileDtl*/
    RSUME_SEQ , RSUME_ORD , FILE_CD , FILE_SEQ , REG_ID , REG_IP , REG_DTM
FROM cx_appctn_rsume_file_dtl
WHERE RSUME_SEQ = #{rsumeSeq}
ORDER BY RSUME_ORD ASC;

SELECT /*WBKBREGISTERMAPPER.getRegisterFinalResultList*/
    mng_stts_cd AS lResultCd
FROM CX_APPCTN_RSUME_DTL a
WHERE APPCTN_SEQ = #{appctnSeq}
AND RSUME_ORD = 3;

SELECT  /*WBKBRegisterMapper.getOptEpisdList*/
    DISTINCT episd
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
ORDER BY episd_seq DESC

SELECT  /*WBKBRegisterMapper.getEpisdSeq*/
       episd_seq
FROM cx_episd_mst
WHERE bsn_cd = #{bsnCd}
AND year = #{year}
AND episd = #{episd}
