mapper.select:
select /* WBLSurveyMapper.selectSurveyList */ a.*, round((total_score/cnt),2) as avg_score, (case when cnt > 2 then round((hkmc_total_score/(cnt-2)),2) when cnt = 0 then round((episd_total_score/episd_cnt),2) else round((total_score/cnt),2) end) as hkmc_avg_score from ( SELECT a.cxstn_srv_seq ,b.year ,b.episd ,b.srv_seq ,a.part_cmpn_nm_1 ,a.part_cmpn_cd_1 ,a.part_cmpn_nm_2 ,a.part_cmpn_cd_2 , a.pic_nm , a.tel_no , a.email ,a.ptcpt_cd ,a.ptcpt_cmpltn_dtm ,a.cmpltn_yn ,a.score , a.send_dtm ,a.reg_id ,(SELECT NAME FROM CO_ADM_MST WHERE ID = A.REG_ID) AS REG_NAME ,a.reg_ip ,a.reg_dtm ,a.mod_id ,(SELECT NAME FROM CO_ADM_MST WHERE ID = A.MOD_ID) AS MOD_NAME ,a.mod_ip ,a.mod_dtm , (select sum(ifnull(score,0)) from cx_appctn_rsume_srv_mst where part_cmpn_cd_1 = a.part_cmpn_cd_1 and cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ptcpt_cd = 'Y') as total_score , (select sum(ifnull(score,0)) - max(ifnull(score,0)) - min(ifnull(score,0)) from cx_appctn_rsume_srv_mst where part_cmpn_cd_1 = a.part_cmpn_cd_1 and cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ptcpt_cd = 'Y') as hkmc_total_score , (select count(score) from cx_appctn_rsume_srv_mst where part_cmpn_cd_1 = a.part_cmpn_cd_1 and cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ptcpt_cd = 'Y') as cnt , (select sum(ifnull(score,0)) from cx_appctn_rsume_srv_mst where cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ptcpt_cd = 'Y') as episd_total_score , (select count(score)from cx_appctn_rsume_srv_mst where cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ptcpt_cd = 'Y') as episd_cnt FROM cx_appctn_rsume_srv_mst A left join cx_cmpn_episd_mst b on a.cxstn_cmpn_episd_seq = b.cxstn_cmpn_episd_seq
) a ORDER BY a.cxstn_srv_seq DESC
LIMIT #{firstIndex}, #{recordCountPerPage}

mapper.insert:
insert /* WBLSurveyMapper.insertSurveyMst */ into cx_appctn_rsume_srv_mst
(
 cxstn_srv_seq
, cxstn_cmpn_episd_seq
, part_cmpn_nm_1
, part_cmpn_cd_1
, part_cmpn_nm_2
, part_cmpn_cd_2
, rprsnt_nm
, bsnm_reg_no
, pic_nm
, tel_no
, email
, srv_rspn_seq
, crtfn_no
, ptcpt_cd
, cmpltn_yn
, ptcpt_cmpltn_dtm
, score
, reg_id
, reg_ip
, reg_dtm
, mod_id
, mod_ip
, mod_dtm)
values (
		#{cxstnSrvSeq}
	   ,#{cxstnCmpnEpisdSeq}
	   ,#{partCmpnNm1}
	   ,#{partCmpnCd1}
	   ,#{partCmpnNm2}
	   ,#{partCmpnCd2}
	   ,#{rprsntNm}
	   ,#{bsnmRegNo}
	   ,#{picNm}
	   ,#{telNo}
	   ,#{email}
	   ,#{srvRspnSeq}
	   ,#{crtfnNo}
	   ,#{ptcptCd}
	   ,#{cmpltnYn}
	   ,#{ptcptCmpltnDtm}
	   ,#{score}
	   ,#{regId}
	   ,#{regIp}
	   ,now()
	   ,#{modId}
	   ,#{modIp}
	   ,now()
	   )

mapper.delete:
delete /* WBLSurveyMapper.deleteSurveyMst */
from cx_appctn_rsume_srv_mst



			cxstn_srv_seq in

				#{delValueList}



			cxstn_srv_seq = #{detailsKey}

mapper.select:
select /* WBLSurveyMapper.selectEpisdList */ a.cxstn_cmpn_episd_seq , a.year , a.episd , a.srv_seq , b.titl , (select count(cxstn_srv_seq) from cx_appctn_rsume_srv_mst where cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ifnull(srv_rspn_seq,0) > 0 ) as rspn_cnt from cx_cmpn_episd_mst a inner join sv_mst b on a.srv_seq = b.srv_seq
where a.cxstn_cmpn_episd_seq = #{cxstnCmpnEpisdSeq}
ORDER BY a.year , a.episd ASC LIMIT #{firstIndex}, #{recordCountPerPage}

mapper.insert:
insert /* WBLSurveyMapper.insertEpisdMst */ into cx_cmpn_episd_mst
