mapper.insert:
insert /* WBLSurveyMapper.insertSurveyMst */ into cx_appctn_rsume_srv_mst
(
 cxstn_srv_seq
, cxstn_cmpn_episd_seq
, part_cmpn_nm_1
, part_cmpn_cd_1
, part_cmpn_nm_2
, part_cmpn_cd_2
, rprsnt_nm
, bsnm_reg_no
, pic_nm
, tel_no
, email
, srv_rspn_seq
, crtfn_no
, ptcpt_cd
, cmpltn_yn
, ptcpt_cmpltn_dtm
, score
, reg_id
, reg_ip
, reg_dtm
, mod_id
, mod_ip
, mod_dtm)
values (
		#{cxstnSrvSeq}
	   ,#{cxstnCmpnEpisdSeq}
	   ,#{partCmpnNm1}
	   ,#{partCmpnCd1}
	   ,#{partCmpnNm2}
	   ,#{partCmpnCd2}
	   ,#{rprsntNm}
	   ,#{bsnmRegNo}
	   ,#{picNm}
	   ,#{telNo}
	   ,#{email}
	   ,#{srvRspnSeq}
	   ,#{crtfnNo}
	   ,#{ptcptCd}
	   ,#{cmpltnYn}
	   ,#{ptcptCmpltnDtm}
	   ,#{score}
	   ,#{regId}
	   ,#{regIp}
	   ,now()
	   ,#{modId}
	   ,#{modIp}
	   ,now()
	   )

mapper.delete:
delete /* WBLSurveyMapper.deleteSurveyMst */
from cx_appctn_rsume_srv_mst



			cxstn_srv_seq in

				#{delValueList}



			cxstn_srv_seq = #{detailsKey}

mapper.select:
select /* WBLSurveyMapper.selectEpisdList */ a.cxstn_cmpn_episd_seq , a.year , a.episd , a.srv_seq , b.titl , (select count(cxstn_srv_seq) from cx_appctn_rsume_srv_mst where cxstn_cmpn_episd_seq = a.cxstn_cmpn_episd_seq and ifnull(srv_rspn_seq,0) > 0 ) as rspn_cnt from cx_cmpn_episd_mst a inner join sv_mst b on a.srv_seq = b.srv_seq
where a.cxstn_cmpn_episd_seq = #{cxstnCmpnEpisdSeq}
ORDER BY a.year , a.episd ASC LIMIT #{firstIndex}, #{recordCountPerPage}

mapper.insert:
insert /* WBLSurveyMapper.insertEpisdMst */ into cx_cmpn_episd_mst
(
 cxstn_cmpn_episd_seq
, year
, episd
, srv_seq
, reg_id
, reg_ip
, reg_dtm
, mod_id
, mod_ip
, mod_dtm)
values (
	   #{cxstnCmpnEpisdSeq}
	   ,#{year}
	   ,#{episd}
	   ,#{srvSeq}
	   ,#{regId}
	   ,#{regIp}
	   ,now()
	   ,#{modId}
	   ,#{modIp}
	   ,now()
	   )

mapper.update:
update /* SVASurveyMapper.updateEpisdMst */ cx_cmpn_episd_mst
set
   srv_seq = #{srvSeq}
  ,mod_id = #{modId}
  ,mod_ip = #{modIp}
  ,mod_dtm = now()
where
	cxstn_cmpn_episd_seq = #{cxstnCmpnEpisdSeq}
